http_endpoint.request_body_spec:
{
  "description": "Release details that needs to be updated",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "description": "Unique identifier of the Release",
            "format": "int64",
            "readOnly": true,
            "example": 10
          },
          "agent_id": {
            "type": "integer",
            "description": "Unique identifier of the agent to whom the Release is assigned",
            "format": "int64",
            "example": 14043616
          },
          "group_id": {
            "type": "integer",
            "description": "Unique identifier of the agent group to which the Release is assigned",
            "format": "int64",
            "example": 10184589
          },
          "priority": {
            "type": "integer",
            "description": "Priority of the Release 1-Low, 2-Medium, 3-High, 4-Urgent",
            "format": "string",
            "example": 1
          },
          "status": {
            "type": "integer",
            "description": "Status identifier of the Release. Open -> 1, On hold -> 2, In Progress -> 3, Incomplete -> 4, Completed -> 5",
            "format": "int32",
            "example": 1
          },
          "release_type": {
            "type": "integer",
            "description": "Type of the Release 1-minor, 2-standard, 3-major, 4-emergency",
            "format": "string",
            "example": 1
          },
          "subject": {
            "type": "string",
            "description": "Subject of the Release",
            "example": "Security Patch Deployment"
          },
          "planned_start_date": {
            "type": "string",
            "description": "Timestamp at which release is starting",
            "format": "date-time",
            "example": "2021-08-01T18:30:00Z"
          },
          "planned_end_date": {
            "type": "string",
            "description": "Timestamp at which release is ending",
            "format": "date-time",
            "example": "2021-08-08T18:45:00Z"
          },
          "work_start_date": {
            "type": "string",
            "description": "Timestamp at which release work started",
            "format": "date-time",
            "example": "2021-08-01T18:30:00Z"
          },
          "work_end_date": {
            "type": "string",
            "description": "Timestamp at which release work ended",
            "format": "date-time",
            "example": "2021-08-08T18:45:00Z"
          },
          "department_id": {
            "type": "integer",
            "description": "Unique ID of the department initiating the Release",
            "format": "int64",
            "example": 10143312
          },
          "category": {
            "type": "string",
            "description": "Category of the Release",
            "example": "Security"
          },
          "sub_category": {
            "type": "string",
            "description": "Sub-category of the Release",
            "example": "Deployment"
          },
          "item_category": {
            "type": "string",
            "description": "Item of the Release",
            "example": "Security"
          },
          "created_at": {
            "type": "string",
            "description": "Timestamp at which Release was created",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-08-12T10:10:20Z"
          },
          "updated_at": {
            "type": "string",
            "description": "Timestamp at which Release was last updated",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-08-12T10:10:20Z"
          },
          "associated_assets": {
            "type": "array",
            "description": "Unique IDs of the assets associated with the Release request",
            "readOnly": true,
            "example": [
              1001,
              1002
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "assets": {
            "type": "array",
            "description": "Assets associated with the Ticket",
            "example": [],
            "items": {
              "type": "object",
              "properties": {
                "id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "id of the asset",
                  "format": "int64",
                  "readOnly": true,
                  "example": 14000234324
                },
                "display_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "display id of the asset that is used for all operations",
                  "format": "int64",
                  "readOnly": true,
                  "example": 1453
                },
                "name": {
                  "type": "string",
                  "description": "Display Name of the Asset",
                  "example": "Hardware-Monitor"
                },
                "description": {
                  "type": "string",
                  "description": "Description of the asset",
                  "example": "28-inch Hardware-Monitor"
                },
                "asset_type_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Id of the asset type.",
                  "format": "int64",
                  "example": 14000284324
                },
                "impact": {
                  "type": "string",
                  "description": "Impact of the asset (accepted values 'high' 'medium' 'low')",
                  "example": "low",
                  "enum": [
                    "low",
                    "medium",
                    "high"
                  ]
                },
                "author_type": {
                  "type": "string",
                  "description": "Asset created by source",
                  "readOnly": true,
                  "example": "User"
                },
                "usage_type": {
                  "type": "string",
                  "description": "Usage type of the asset  (accepted values are 'permanent' & 'loaner')",
                  "example": "loaner",
                  "enum": [
                    "permanent",
                    "loaner"
                  ]
                },
                "asset_tag": {
                  "type": "string",
                  "description": "Asset tag of the asset",
                  "example": "wxyzabcdefghij"
                },
                "user_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Used by of the asset",
                  "format": "int64",
                  "example": 14000234324
                },
                "department_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Department of the asset",
                  "format": "int64",
                  "example": 14000232343
                },
                "location_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Location of the asset",
                  "format": "int64",
                  "example": 140006394857
                },
                "agent_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Managed by of the asset",
                  "format": "int64",
                  "example": 14000234324
                },
                "group_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Managed by group of the asset",
                  "format": "int64",
                  "example": 14000234939
                },
                "assigned_on": {
                  "type": "string",
                  "description": "Date and time when the asset was assigned",
                  "format": "date-time",
                  "example": "2021-11-24T11:30:00Z"
                },
                "created_at": {
                  "type": "string",
                  "description": "Timestamp at which the asset was created",
                  "format": "date-time",
                  "readOnly": true,
                  "example": "2021-11-24T11:30:00Z"
                },
                "updated_at": {
                  "type": "string",
                  "description": "Timestamp at which the asset was last modified",
                  "format": "date-time",
                  "readOnly": true,
                  "example": "2021-11-24T11:30:00Z"
                },
                "type_fields": {
                  "type": "object",
                  "additionalProperties": true,
                  "description": "Fields specific to every asset type (Every asset will have default base fields and type specific fields)",
                  "example": {
                    "field1": "Value 1",
                    "field2": "Value 2"
                  }
                }
              }
            }
          },
          "associated_changes": {
            "type": "array",
            "description": "Unique IDs of the Changes associated with the Release",
            "readOnly": true,
            "example": [
              1001,
              1002
            ],
            "items": {
              "type": "integer",
              "format": "int64"
            }
          },
          "custom_fields": {
            "type": "object",
            "additionalProperties": {
              "type": "string"
            },
            "description": "Key value pairs containing the names and values of custom fields",
            "example": {
              "field1": "Some Value"
            }
          },
          "planning_fields": {
            "type": "object",
            "properties": {
              "reason_for_change": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Reason for change",
                    "example": "<p>Reason for change</p>"
                  },
                  "description_text": {
                    "type": "string",
                    "description": "Reason for change in plain text format",
                    "readOnly": true,
                    "example": "Reason for Change"
                  }
                },
                "example": {
                  "description": "<div>Reason for change</div>",
                  "description_text": "Reason for change"
                }
              },
              "impact": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Impact",
                    "example": "<p>Impact</p>"
                  },
                  "description_text": {
                    "type": "string",
                    "description": "Impact in plain text format",
                    "readOnly": true,
                    "example": "Impact"
                  }
                },
                "example": {
                  "description": "<div>Impact</div>",
                  "description_text": "Impact"
                }
              },
              "rollout_plan": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Rollout Plan",
                    "example": "<div>Rollout plan</div>"
                  },
                  "description_text": {
                    "type": "string",
                    "description": "Rollout plan in plain text format",
                    "readOnly": true,
                    "example": "Rollout Plan"
                  }
                },
                "example": {
                  "description": "<div>Rollout plan</div>",
                  "description_text": "Rollout Plan"
                }
              },
              "backout_plan": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Backout Plan",
                    "example": "<div>Backout plan</div>"
                  },
                  "description_text": {
                    "type": "string",
                    "description": "Backout plan in plain text format",
                    "readOnly": true,
                    "example": "Backout Plan"
                  }
                },
                "example": {
                  "description": "<div>Backout plan</div>",
                  "description_text": "Backout Plan"
                }
              }
            },
            "example": {
              "reason_for_change": {
                "description": "<div>Reason for change</div>",
                "description_text": "Reason for change"
              },
              "impact": {
                "description": "<div>Impact</div>",
                "description_text": "Impact"
              },
              "rollout_plan": {
                "description": "<div>Rollout plan</div>",
                "description_text": "Rollout Plan"
              },
              "backout_plan": {
                "description": "<div>Backout plan</div>",
                "description_text": "Backout Plan"
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
