http_endpoint.request_body_spec:
{
  "description": "tassk details that needs to be updated",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "created_by": {
            "type": "integer",
            "description": "Unique ID of the user who created the task",
            "format": "int64",
            "readOnly": true,
            "example": 14000048691
          },
          "agent_id": {
            "type": "integer",
            "description": "Id of the agent to whom the task is assigned",
            "format": "int64",
            "readOnly": true,
            "example": 14000043616
          },
          "id": {
            "type": "integer",
            "description": "Unique ID of the task",
            "format": "int64",
            "readOnly": true,
            "example": 48
          },
          "status": {
            "type": "integer",
            "description": "Status of the task, 1-Open, 2-In Progress, 3-Completed",
            "format": "int64",
            "readOnly": true,
            "example": 1,
            "enum": [
              1,
              2,
              3,
              4
            ]
          },
          "parent_id": {
            "type": "integer",
            "description": "Unique ID of the parent entity to which the task belongs",
            "format": "int64",
            "readOnly": true,
            "example": 589
          },
          "parent_type": {
            "type": "string",
            "description": "Type of the parent entity - [Ticket, Problem, Change, Release, Project]",
            "example": "Ticket"
          },
          "due_date": {
            "type": "string",
            "description": "Due date of the task",
            "format": "date-time",
            "example": "2021-11-24T11:30:00Z"
          },
          "notify_before": {
            "type": "integer",
            "description": "Time in seconds before which notification is sent prior to due date",
            "format": "int64",
            "example": 3600
          },
          "title": {
            "type": "string",
            "description": "Title of the task",
            "example": "Renew license"
          },
          "description": {
            "type": "string",
            "description": "Description of the task",
            "example": "Renew Software license"
          },
          "created_at": {
            "type": "string",
            "description": "Timestamp at which the task was created",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          },
          "updated_at": {
            "type": "string",
            "description": "Timestamp at which the task was updated",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          },
          "closed_at": {
            "type": "string",
            "description": "Timestamp at which the task was closed",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          },
          "group_id": {
            "type": "integer",
            "description": "Unique ID of the group to which the task is assigned",
            "format": "int64",
            "readOnly": true,
            "example": 14000184589
          },
          "start_date": {
            "type": "string",
            "description": "Timestamp at which the task is started",
            "format": "date-time",
            "example": "2021-11-22T16:58:45Z"
          }
        }
      }
    }
  },
  "required": true
}
