http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "description": "Unique ID of the Product",
            "format": "int64",
            "readOnly": true,
            "example": 14000234324
          },
          "name": {
            "type": "string",
            "description": "Name of the Product",
            "example": "Apple MacBook Air 13"
          },
          "asset_type_id": {
            "type": "integer",
            "description": "Asset Type id of the Product",
            "format": "int64",
            "example": 14000234243
          },
          "manufacturer": {
            "type": "string",
            "description": "Manufacturer Name - Free Text",
            "example": "Apple"
          },
          "status_id": {
            "type": "integer",
            "description": "status can be\n  `1` - In Production\n  `2` - In Pipeline\n  `3` - Retired\n",
            "format": "int64",
            "example": 1,
            "enum": [
              1,
              2,
              3
            ]
          },
          "mode_of_procurement_id": {
            "type": "integer",
            "description": "mode of procurement can be\n  `1` - Buy\n  `2` - Lease\n  `3` - Both\n",
            "format": "int64",
            "example": 1,
            "enum": [
              1,
              2,
              3
            ]
          },
          "depreciation_type_id": {
            "type": "integer",
            "format": "int64",
            "example": 140009854
          },
          "description": {
            "type": "string",
            "description": "Unique identifier for the depreciation type used",
            "example": "Apple"
          }
        }
      }
    }
  },
  "required": true
}
