http_endpoint.request_body_spec:
{
  "description": "Problem details that needs to be updated",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "description": "Unique identifier of the Problem",
            "format": "int64",
            "readOnly": true,
            "example": 1001
          },
          "agent_id": {
            "type": "integer",
            "description": "Unique identifier of the agent to whom the Problem is assigned",
            "format": "int64",
            "example": 34523423
          },
          "requester_id": {
            "type": "integer",
            "description": "Unique identifier of the user who raised the Problem",
            "format": "int64",
            "example": 193845793
          },
          "group_id": {
            "type": "integer",
            "description": "Unique identifier of the agent group to which the Problem is assigned",
            "format": "int64",
            "example": 12943209
          },
          "priority": {
            "type": "integer",
            "description": "Priority of the Problem 1-Low, 2-Medium, 3-High, 4-Urgent",
            "format": "int64",
            "example": 1,
            "enum": [
              1,
              2,
              3,
              4
            ]
          },
          "impact": {
            "type": "integer",
            "description": "Impact of the Problem 1-Low, 2-Medium, 3-High",
            "format": "int64",
            "example": 1,
            "enum": [
              1,
              2,
              3
            ]
          },
          "status": {
            "type": "integer",
            "description": "Status identifier of the Problem. Open -> 1, Change Requested -> 2, Closed -> 3",
            "format": "int32",
            "example": 1
          },
          "subject": {
            "type": "string",
            "description": "Subject of the Problem",
            "example": "Unable to reach email server"
          },
          "due_by": {
            "type": "string",
            "description": "Timestamp at which Problem's resolution is due",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-06-25T07:16:00Z"
          },
          "department_id": {
            "type": "integer",
            "description": "Unique ID of the department initiating the Problem",
            "format": "int64",
            "example": 14002384
          },
          "category": {
            "type": "string",
            "description": "Category of the Problem",
            "example": "Customer Support"
          },
          "sub_category": {
            "type": "string",
            "description": "Sub-category of the Problem",
            "example": "Multi-DRM and Rights Management"
          },
          "item_category": {
            "type": "string",
            "description": "Item of the Problem",
            "example": "Media Manager"
          },
          "known_error": {
            "type": "boolean",
            "description": "true if the Problem is a known error, false otherwise",
            "example": true
          },
          "created_at": {
            "type": "string",
            "description": "Timestamp at which Problem was created",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-10-12T15:01:27Z"
          },
          "updated_at": {
            "type": "string",
            "description": "Timestamp at which Problem was last updated",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-10-12T15:01:27Z"
          },
          "assets": {
            "type": "array",
            "description": "Assets associated with the Ticket",
            "example": [],
            "items": {
              "type": "object",
              "properties": {
                "id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "id of the asset",
                  "format": "int64",
                  "readOnly": true,
                  "example": 14000234324
                },
                "display_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "display id of the asset that is used for all operations",
                  "format": "int64",
                  "readOnly": true,
                  "example": 1453
                },
                "name": {
                  "type": "string",
                  "description": "Display Name of the Asset",
                  "example": "Hardware-Monitor"
                },
                "description": {
                  "type": "string",
                  "description": "Description of the asset",
                  "example": "28-inch Hardware-Monitor"
                },
                "asset_type_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Id of the asset type.",
                  "format": "int64",
                  "example": 14000284324
                },
                "impact": {
                  "type": "string",
                  "description": "Impact of the asset (accepted values 'high' 'medium' 'low')",
                  "example": "low",
                  "enum": [
                    "low",
                    "medium",
                    "high"
                  ]
                },
                "author_type": {
                  "type": "string",
                  "description": "Asset created by source",
                  "readOnly": true,
                  "example": "User"
                },
                "usage_type": {
                  "type": "string",
                  "description": "Usage type of the asset  (accepted values are 'permanent' & 'loaner')",
                  "example": "loaner",
                  "enum": [
                    "permanent",
                    "loaner"
                  ]
                },
                "asset_tag": {
                  "type": "string",
                  "description": "Asset tag of the asset",
                  "example": "wxyzabcdefghij"
                },
                "user_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Used by of the asset",
                  "format": "int64",
                  "example": 14000234324
                },
                "department_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Department of the asset",
                  "format": "int64",
                  "example": 14000232343
                },
                "location_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Location of the asset",
                  "format": "int64",
                  "example": 140006394857
                },
                "agent_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Managed by of the asset",
                  "format": "int64",
                  "example": 14000234324
                },
                "group_id": {
                  "minimum": 1,
                  "type": "integer",
                  "description": "Managed by group of the asset",
                  "format": "int64",
                  "example": 14000234939
                },
                "assigned_on": {
                  "type": "string",
                  "description": "Date and time when the asset was assigned",
                  "format": "date-time",
                  "example": "2021-11-24T11:30:00Z"
                },
                "created_at": {
                  "type": "string",
                  "description": "Timestamp at which the asset was created",
                  "format": "date-time",
                  "readOnly": true,
                  "example": "2021-11-24T11:30:00Z"
                },
                "updated_at": {
                  "type": "string",
                  "description": "Timestamp at which the asset was last modified",
                  "format": "date-time",
                  "readOnly": true,
                  "example": "2021-11-24T11:30:00Z"
                },
                "type_fields": {
                  "type": "object",
                  "additionalProperties": true,
                  "description": "Fields specific to every asset type (Every asset will have default base fields and type specific fields)",
                  "example": {
                    "field1": "Value 1",
                    "field2": "Value 2"
                  }
                }
              }
            }
          },
          "attachments": {
            "type": "array",
            "description": "URLs of attachments attached to the Problem request",
            "readOnly": true,
            "example": [
              "https://s3.amazonaws.com/fs/image1.jpg",
              "https://s3.amazonaws.com/fs/image2.jpg"
            ],
            "items": {
              "type": "string",
              "format": "URL"
            }
          },
          "custom_fields": {
            "type": "object",
            "additionalProperties": {
              "type": "string"
            },
            "description": "Key value pairs containing the names and values of custom fields",
            "example": {
              "field1": "Sample value"
            }
          },
          "analysis_fields": {
            "type": "object",
            "properties": {
              "problem_cause": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Cause of the Problem",
                    "example": "Router malfunction"
                  },
                  "attachments": {
                    "type": "array",
                    "description": "URLs of attachments attached for Problem Cause",
                    "example": [
                      "https://s3.amazonaws.com/fs/doc1.pdf",
                      "https://s3.amazonaws.com/fs/doc2.pdf"
                    ],
                    "items": {
                      "type": "string",
                      "format": "URL"
                    }
                  }
                },
                "example": {
                  "description": "Router malfunction"
                }
              },
              "problem_symptoms": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Symptoms of the Problem",
                    "example": "Cannot connect mobile devices to Wi-fi"
                  },
                  "attachments": {
                    "type": "array",
                    "description": "URLs of attachments attached for Problem Symptom",
                    "example": [
                      "https://s3.amazonaws.com/fs/doc1.pdf",
                      "https://s3.amazonaws.com/fs/doc2.pdf"
                    ],
                    "items": {
                      "type": "string",
                      "format": "URL"
                    }
                  }
                },
                "example": {
                  "description": "Cannot connect mobile devices to Wi-fi"
                }
              },
              "problem_impact": {
                "type": "object",
                "properties": {
                  "description": {
                    "type": "string",
                    "description": "Impact of the Problem",
                    "example": "Affects Wi-fi connectivity in Block A"
                  },
                  "attachments": {
                    "type": "array",
                    "description": "URLs of attachments attached for Problem Impact",
                    "example": [
                      "https://s3.amazonaws.com/fs/doc1.pdf",
                      "https://s3.amazonaws.com/fs/doc2.pdf"
                    ],
                    "items": {
                      "type": "string",
                      "format": "URL"
                    }
                  }
                },
                "example": {
                  "description": "Affects Wi-fi connectivity in Block A"
                }
              }
            },
            "example": {
              "problem_cause": {
                "description": "Router malfunction"
              },
              "problem_symptoms": {
                "description": "Cannot connect mobile devices to Wi-fi"
              },
              "problem_impact": {
                "description": "Affects Wi-fi connectivity in Block A"
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
