http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "summary": "Creating a gist",
          "value": {
            "description": "Example of a gist",
            "files": {
              "README.md": {
                "content": "Hello World"
              }
            },
            "public": false
          }
        }
      },
      "schema": {
        "properties": {
          "description": {
            "description": "Description of the gist",
            "example": "Example Ruby script",
            "type": "string"
          },
          "files": {
            "additionalProperties": {
              "properties": {
                "content": {
                  "description": "Content of the file",
                  "readOnly": false,
                  "type": "string"
                }
              },
              "required": [
                "content"
              ],
              "type": "object"
            },
            "description": "Names and content for the files that make up the gist",
            "example": {
              "hello.rb": {
                "content": "puts \"Hello, World!\""
              }
            },
            "type": "object"
          },
          "public": {
            "oneOf": [
              {
                "default": false,
                "description": "Flag indicating whether the gist is public",
                "example": true,
                "type": "boolean"
              },
              {
                "default": "false",
                "enum": [
                  "true",
                  "false"
                ],
                "example": "true",
                "type": "string"
              }
            ]
          }
        },
        "required": [
          "files"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
