http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "advanced-example": {
          "summary": "Advanced example",
          "value": {
            "auto_merge": false,
            "description": "Deploy request from hubot",
            "payload": "{ \"deploy\": \"migrate\" }",
            "ref": "topic-branch",
            "required_contexts": [
              "ci/janky",
              "security/brakeman"
            ]
          }
        },
        "simple-example": {
          "summary": "Simple example",
          "value": {
            "description": "Deploy request from hubot",
            "payload": "{ \"deploy\": \"migrate\" }",
            "ref": "topic-branch"
          }
        }
      },
      "schema": {
        "properties": {
          "auto_merge": {
            "default": true,
            "description": "Attempts to automatically merge the default branch into the requested ref, if it's behind the default branch.",
            "type": "boolean"
          },
          "description": {
            "default": "",
            "description": "Short description of the deployment.",
            "nullable": true,
            "type": "string"
          },
          "environment": {
            "default": "production",
            "description": "Name for the target deployment environment (e.g., `production`, `staging`, `qa`).",
            "type": "string"
          },
          "payload": {
            "oneOf": [
              {
                "additionalProperties": true,
                "type": "object"
              },
              {
                "default": "",
                "description": "JSON payload with extra information about the deployment.",
                "type": "string"
              }
            ]
          },
          "production_environment": {
            "description": "Specifies if the given environment is one that end-users directly interact with. Default: `true` when `environment` is `production` and `false` otherwise.",
            "type": "boolean"
          },
          "ref": {
            "description": "The ref to deploy. This can be a branch, tag, or SHA.",
            "type": "string"
          },
          "required_contexts": {
            "description": "The [status](https://docs.github.com/enterprise-server@3.8/rest/commits/statuses) contexts to verify against commit status checks. If you omit this parameter, GitHub verifies all unique contexts before creating a deployment. To bypass checking entirely, pass an empty array. Defaults to all unique contexts.",
            "items": {
              "type": "string"
            },
            "type": "array"
          },
          "task": {
            "default": "deploy",
            "description": "Specifies a task to execute (e.g., `deploy` or `deploy:migrations`).",
            "type": "string"
          },
          "transient_environment": {
            "default": false,
            "description": "Specifies if the given environment is specific to the deployment and will no longer exist at some point in the future. Default: `false`",
            "type": "boolean"
          }
        },
        "required": [
          "ref"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
