http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "summary": "Create a new card",
          "value": {
            "note": "Add payload for delete Project column"
          }
        }
      },
      "schema": {
        "oneOf": [
          {
            "properties": {
              "note": {
                "description": "The project card's note",
                "example": "Update all gems",
                "nullable": true,
                "type": "string"
              }
            },
            "required": [
              "note"
            ],
            "type": "object"
          },
          {
            "properties": {
              "content_id": {
                "description": "The unique identifier of the content associated with the card",
                "example": 42,
                "type": "integer"
              },
              "content_type": {
                "description": "The piece of content associated with the card",
                "example": "PullRequest",
                "type": "string"
              }
            },
            "required": [
              "content_id",
              "content_type"
            ],
            "type": "object"
          }
        ]
      }
    }
  },
  "required": true
}
