http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "name": "Expensive hardware runners",
            "runners": [
              9,
              2
            ],
            "selected_repository_ids": [
              32,
              91
            ],
            "visibility": "selected"
          }
        }
      },
      "schema": {
        "properties": {
          "allows_public_repositories": {
            "default": false,
            "description": "Whether the runner group can be used by `public` repositories.",
            "type": "boolean"
          },
          "name": {
            "description": "Name of the runner group.",
            "type": "string"
          },
          "restricted_to_workflows": {
            "default": false,
            "description": "If `true`, the runner group will be restricted to running only the workflows specified in the `selected_workflows` array.",
            "type": "boolean"
          },
          "runners": {
            "description": "List of runner IDs to add to the runner group.",
            "items": {
              "description": "Unique identifier of the runner.",
              "type": "integer"
            },
            "type": "array"
          },
          "selected_repository_ids": {
            "description": "List of repository IDs that can access the runner group.",
            "items": {
              "description": "Unique identifier of the repository.",
              "type": "integer"
            },
            "type": "array"
          },
          "selected_workflows": {
            "description": "List of workflows the runner group should be allowed to run. This setting will be ignored unless `restricted_to_workflows` is set to `true`.",
            "items": {
              "description": "Name of workflow the runner group should be allowed to run. Note that a ref, tag, or long SHA is required.",
              "example": "octo-org/octo-repo/.github/workflows/deploy.yaml@main",
              "type": "string"
            },
            "type": "array"
          },
          "visibility": {
            "default": "all",
            "description": "Visibility of a runner group. You can select all repositories, select individual repositories, or limit access to private repositories.",
            "enum": [
              "selected",
              "all",
              "private"
            ],
            "type": "string"
          }
        },
        "required": [
          "name"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
