http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "allowed_actions": "selected",
            "enabled_organizations": "all"
          }
        }
      },
      "schema": {
        "properties": {
          "allowed_actions": {
            "description": "The permissions policy that controls the actions that are allowed to run.",
            "enum": [
              "all",
              "local_only",
              "selected"
            ],
            "type": "string"
          },
          "enabled_organizations": {
            "description": "The policy that controls the organizations in the enterprise that are allowed to run GitHub Actions.",
            "enum": [
              "all",
              "none",
              "selected"
            ],
            "type": "string"
          }
        },
        "required": [
          "enabled_organizations"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
