http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "example-for-creating-a-file": {
          "summary": "Example for creating a file",
          "value": {
            "committer": {
              "email": "octocat@github.com",
              "name": "Monalisa Octocat"
            },
            "content": "bXkgbmV3IGZpbGUgY29udGVudHM=",
            "message": "my commit message"
          }
        },
        "example-for-updating-a-file": {
          "summary": "Example for updating a file",
          "value": {
            "committer": {
              "email": "octocat@github.com",
              "name": "Monalisa Octocat"
            },
            "content": "bXkgdXBkYXRlZCBmaWxlIGNvbnRlbnRz",
            "message": "a new commit message",
            "sha": "95b966ae1c166bd92f8ae7d1c313e738c731dfc3"
          }
        }
      },
      "schema": {
        "properties": {
          "author": {
            "description": "The author of the file. Default: The `committer` or the authenticated user if you omit `committer`.",
            "properties": {
              "date": {
                "example": "\"2013-01-15T17:13:22+05:00\"",
                "type": "string"
              },
              "email": {
                "description": "The email of the author or committer of the commit. You'll receive a `422` status code if `email` is omitted.",
                "type": "string"
              },
              "name": {
                "description": "The name of the author or committer of the commit. You'll receive a `422` status code if `name` is omitted.",
                "type": "string"
              }
            },
            "required": [
              "name",
              "email"
            ],
            "type": "object"
          },
          "branch": {
            "description": "The branch name. Default: the repository\u2019s default branch (usually `master`)",
            "type": "string"
          },
          "committer": {
            "description": "The person that committed the file. Default: the authenticated user.",
            "properties": {
              "date": {
                "example": "\"2013-01-05T13:13:22+05:00\"",
                "type": "string"
              },
              "email": {
                "description": "The email of the author or committer of the commit. You'll receive a `422` status code if `email` is omitted.",
                "type": "string"
              },
              "name": {
                "description": "The name of the author or committer of the commit. You'll receive a `422` status code if `name` is omitted.",
                "type": "string"
              }
            },
            "required": [
              "name",
              "email"
            ],
            "type": "object"
          },
          "content": {
            "description": "The new file content, using Base64 encoding.",
            "type": "string"
          },
          "message": {
            "description": "The commit message.",
            "type": "string"
          },
          "sha": {
            "description": "**Required if you are updating a file**. The blob SHA of the file being replaced.",
            "type": "string"
          }
        },
        "required": [
          "message",
          "content"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
