http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "summary": "Change the name, state, and permissions for a project",
          "value": {
            "name": "Week One Sprint",
            "organization_permission": "write",
            "state": "open"
          }
        }
      },
      "schema": {
        "properties": {
          "body": {
            "description": "Body of the project",
            "example": "This project represents the sprint of the first week in January",
            "nullable": true,
            "type": "string"
          },
          "name": {
            "description": "Name of the project",
            "example": "Week One Sprint",
            "type": "string"
          },
          "organization_permission": {
            "description": "The baseline permission that all organization members have on this project",
            "enum": [
              "read",
              "write",
              "admin",
              "none"
            ],
            "type": "string"
          },
          "private": {
            "description": "Whether or not this project can be seen by everyone.",
            "type": "boolean"
          },
          "state": {
            "description": "State of the project; either 'open' or 'closed'",
            "example": "open",
            "type": "string"
          }
        },
        "type": "object"
      }
    }
  },
  "required": false
}
