http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "default": {
          "value": {
            "allowed_actions": "selected",
            "enabled": true
          }
        }
      },
      "schema": {
        "properties": {
          "allowed_actions": {
            "description": "The permissions policy that controls the actions that are allowed to run.",
            "enum": [
              "all",
              "local_only",
              "selected"
            ],
            "type": "string"
          },
          "enabled": {
            "description": "Whether GitHub Actions is enabled on the repository.",
            "type": "boolean"
          }
        },
        "required": [
          "enabled"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
