http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "attachments",
          "channelAccountId",
          "integrationThreadId",
          "messageDirection",
          "recipients",
          "senders",
          "text",
          "timestamp"
        ],
        "type": "object",
        "properties": {
          "messageDirection": {
            "type": "string",
            "enum": [
              "INCOMING",
              "OUTGOING"
            ]
          },
          "attachments": {
            "type": "array",
            "items": {
              "oneOf": [
                {
                  "title": "FILE",
                  "required": [
                    "fileId",
                    "type"
                  ],
                  "type": "object",
                  "properties": {
                    "fileUsageType": {
                      "type": "string"
                    },
                    "type": {
                      "type": "string",
                      "default": "FILE",
                      "enum": [
                        "FILE"
                      ]
                    },
                    "fileId": {
                      "type": "string"
                    }
                  }
                },
                {
                  "type": "object",
                  "description": "Schema reference broken: #/components/schemas/LocationAttachment",
                  "additionalProperties": true,
                  "x-moar-broken-reference": "#/components/schemas/LocationAttachment"
                },
                {
                  "type": "object",
                  "description": "Schema reference broken: #/components/schemas/ContactAttachment",
                  "additionalProperties": true,
                  "x-moar-broken-reference": "#/components/schemas/ContactAttachment"
                },
                {
                  "type": "object",
                  "description": "Schema reference broken: #/components/schemas/UnsupportedContentAttachment",
                  "additionalProperties": true,
                  "x-moar-broken-reference": "#/components/schemas/UnsupportedContentAttachment"
                },
                {
                  "type": "object",
                  "description": "Schema reference broken: #/components/schemas/MessageHeaderAttachment",
                  "additionalProperties": true,
                  "x-moar-broken-reference": "#/components/schemas/MessageHeaderAttachment"
                },
                {
                  "type": "object",
                  "description": "Schema reference broken: #/components/schemas/QuickRepliesAttachment",
                  "additionalProperties": true,
                  "x-moar-broken-reference": "#/components/schemas/QuickRepliesAttachment"
                },
                {
                  "type": "object",
                  "description": "Schema reference broken: #/components/schemas/SocialMetadataIntegrationAttachment",
                  "additionalProperties": true,
                  "x-moar-broken-reference": "#/components/schemas/SocialMetadataIntegrationAttachment"
                }
              ]
            }
          },
          "preResolvedContacts": {
            "required": [
              "contacts"
            ],
            "type": "object",
            "properties": {
              "contacts": {
                "type": "array",
                "items": {
                  "required": [
                    "contactPropertiesLeadingToMatch",
                    "contactVid"
                  ],
                  "type": "object",
                  "properties": {
                    "contactVid": {
                      "type": "integer",
                      "format": "int32"
                    },
                    "contactPropertiesLeadingToMatch": {
                      "type": "array",
                      "items": {
                        "type": "string"
                      }
                    }
                  }
                }
              }
            }
          },
          "recipients": {
            "type": "array",
            "items": {
              "required": [
                "deliveryIdentifier"
              ],
              "type": "object",
              "properties": {
                "name": {
                  "type": "string"
                },
                "deliveryIdentifier": {
                  "required": [
                    "type",
                    "value"
                  ],
                  "type": "object",
                  "properties": {
                    "type": {
                      "type": "string",
                      "description": "The type of identifier. HS_EMAIL_ADDRESS for email addresses; HS_PHONE_NUMBER for a phone number; CHANNEL_SPECIFIC_OPAQUE_ID for channels that use their own proprietary identifiers, like Facebook Messenger or LiveChat."
                    },
                    "value": {
                      "type": "string",
                      "description": "A string representation of the PublicDeliveryIdentifier, either an an E.164 phone number, an email address, or a channel-specific identifier."
                    }
                  }
                }
              }
            }
          },
          "integrationThreadId": {
            "type": "string"
          },
          "integrationIdempotencyId": {
            "type": "string"
          },
          "text": {
            "type": "string"
          },
          "richText": {
            "type": "string"
          },
          "channelAccountId": {
            "type": "string"
          },
          "senders": {
            "type": "array",
            "items": {
              "required": [
                "deliveryIdentifier"
              ],
              "type": "object",
              "properties": {
                "name": {
                  "type": "string"
                },
                "deliveryIdentifier": {
                  "required": [
                    "type",
                    "value"
                  ],
                  "type": "object",
                  "properties": {
                    "type": {
                      "type": "string",
                      "description": "The type of identifier. HS_EMAIL_ADDRESS for email addresses; HS_PHONE_NUMBER for a phone number; CHANNEL_SPECIFIC_OPAQUE_ID for channels that use their own proprietary identifiers, like Facebook Messenger or LiveChat."
                    },
                    "value": {
                      "type": "string",
                      "description": "A string representation of the PublicDeliveryIdentifier, either an an E.164 phone number, an email address, or a channel-specific identifier."
                    }
                  }
                }
              }
            }
          },
          "inReplyToId": {
            "type": "string"
          },
          "timestamp": {
            "type": "string",
            "format": "date-time"
          }
        }
      }
    }
  },
  "required": true
}
