http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "accountName",
          "deliveryIdentifier"
        ],
        "type": "object",
        "properties": {
          "accountName": {
            "type": "string"
          },
          "deliveryIdentifier": {
            "required": [
              "type",
              "value"
            ],
            "type": "object",
            "properties": {
              "type": {
                "type": "string",
                "description": "The type of identifier. HS_EMAIL_ADDRESS for email addresses; HS_PHONE_NUMBER for a phone number; CHANNEL_SPECIFIC_OPAQUE_ID for channels that use their own proprietary identifiers, like Facebook Messenger or LiveChat."
              },
              "value": {
                "type": "string",
                "description": "A string representation of the PublicDeliveryIdentifier, either an an E.164 phone number, an email address, or a channel-specific identifier."
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
