http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "contentId",
          "variationName"
        ],
        "type": "object",
        "properties": {
          "variationName": {
            "type": "string"
          },
          "contentId": {
            "type": "string",
            "description": "ID of the object to test."
          }
        },
        "description": "Request body object for creating A/B tests.",
        "example": {
          "contentId": "7",
          "variantName": "AB Test - Number 7"
        }
      }
    }
  },
  "required": true
}
