http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "inputs"
        ],
        "type": "object",
        "properties": {
          "inputs": {
            "type": "array",
            "description": "List of marketing event details to create or update",
            "items": {
              "required": [
                "email",
                "interactionDateTime"
              ],
              "type": "object",
              "properties": {
                "contactProperties": {
                  "type": "object",
                  "additionalProperties": {
                    "type": "string"
                  }
                },
                "properties": {
                  "type": "object",
                  "additionalProperties": {
                    "type": "string"
                  }
                },
                "email": {
                  "type": "string",
                  "description": "The email address of the contact in HubSpot to associate with the event."
                },
                "interactionDateTime": {
                  "type": "integer",
                  "description": "Timestamp in milliseconds at which the contact subscribed to the event.",
                  "format": "int64"
                }
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
