http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "You can create an phone switch",
        "type": "object",
        "title": "Create Phone Switch Request Payload",
        "nullable": true,
        "properties": {
          "phone": {
            "type": "string",
            "description": "Phone number in E.164 format, that will receive the SMS to continue the conversation in the Messenger.",
            "example": "+1 1234567890"
          },
          "custom_attributes": {
            "type": "object",
            "description": "Schema reference broken: #/components/schemas/custom_attributes",
            "additionalProperties": true,
            "x-moar-broken-reference": "#/components/schemas/custom_attributes"
          }
        },
        "required": [
          "phone"
        ]
      },
      "examples": {
        "successful": {
          "summary": "successful",
          "value": {
            "phone": "+353832345678",
            "custom_attributes": {
              "issue_type": "Billing",
              "priority": "High"
            }
          }
        },
        "bad_request_-_exception_sending_sms": {
          "summary": "bad request - exception sending sms",
          "value": {
            "phone": "+353832345678",
            "custom_attributes": {
              "issue_type": "Billing",
              "priority": "High"
            }
          }
        },
        "bad_request_-_invalid_number": {
          "summary": "bad request - invalid number",
          "value": {
            "phone": "+353832345678",
            "custom_attributes": {
              "issue_type": "Billing",
              "priority": "High"
            }
          }
        },
        "unprocessable_entity": {
          "summary": "unprocessable entity",
          "value": {
            "phone": "+40241100100",
            "custom_attributes": {
              "issue_type": "Billing",
              "priority": "High"
            }
          }
        }
      }
    }
  }
}
