http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "id",
          "consent_type"
        ],
        "properties": {
          "id": {
            "type": "string",
            "description": "The unique identifier for the subscription which is given by Intercom",
            "example": "37846"
          },
          "consent_type": {
            "type": "string",
            "description": "The consent_type of a subscription, opt_out or opt_in.",
            "example": "opt_in"
          }
        }
      },
      "examples": {
        "successful": {
          "summary": "Successful",
          "value": {
            "id": 108,
            "consent_type": "opt_in"
          }
        },
        "contact_not_found": {
          "summary": "Contact not found",
          "value": {
            "id": 112,
            "consent_type": "opt_in"
          }
        },
        "resource_not_found": {
          "summary": "Resource not found",
          "value": {
            "id": "invalid_id",
            "consent_type": "opt_in"
          }
        }
      }
    }
  }
}
