http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Search using Intercoms Search APIs.",
        "type": "object",
        "title": "Search data",
        "properties": {
          "query": {
            "oneOf": [
              {
                "title": "Single Filter Search Request",
                "description": "Search using Intercoms Search APIs with a single filter.",
                "type": "object",
                "properties": {
                  "field": {
                    "type": "string",
                    "description": "The accepted field that you want to search on.",
                    "example": "created_at"
                  },
                  "operator": {
                    "type": "string",
                    "enum": [
                      "=",
                      "!=",
                      "IN",
                      "NIN",
                      "<",
                      ">",
                      "~",
                      "!~",
                      "^",
                      "$"
                    ],
                    "description": "The accepted operators you can use to define how you want to search for the value.",
                    "example": ">"
                  },
                  "value": {
                    "oneOf": [
                      {
                        "type": "string"
                      },
                      {
                        "type": "integer"
                      },
                      {
                        "type": "array",
                        "items": {
                          "type": "string"
                        }
                      },
                      {
                        "type": "array",
                        "items": {
                          "type": "integer"
                        }
                      }
                    ],
                    "description": "The value that you want to search on.",
                    "example": "73732934",
                    "nullable": true
                  }
                }
              },
              {
                "title": "Multiple Filter Search Request",
                "description": "Search using Intercoms Search APIs with more than one filter.",
                "type": "object",
                "properties": {
                  "operator": {
                    "type": "string",
                    "enum": [
                      "AND",
                      "OR"
                    ],
                    "description": "An operator to allow boolean inspection between multiple fields.",
                    "example": "AND"
                  },
                  "value": {
                    "type": "array",
                    "items": {
                      "title": "Multiple or Single Filter Search Request",
                      "oneOf": [
                        {
                          "title": "Multiple Filter Search Request",
                          "description": "Search using Intercoms Search APIs with more than one filter.",
                          "type": "object",
                          "properties": {
                            "operator": {
                              "type": "string",
                              "enum": [
                                "AND",
                                "OR"
                              ],
                              "description": "An operator to allow boolean inspection between multiple fields.",
                              "example": "AND"
                            },
                            "value": {
                              "type": "array",
                              "items": {
                                "title": "Multiple or Single Filter Search Request",
                                "oneOf": [
                                  {
                                    "$ref": "#/components/schemas/multiple_filter_search_request",
                                    "has_circular_reference": true
                                  },
                                  {
                                    "title": "Single Filter Search Request",
                                    "description": "Search using Intercoms Search APIs with a single filter.",
                                    "type": "object",
                                    "properties": {
                                      "field": {
                                        "type": "string",
                                        "description": "The accepted field that you want to search on.",
                                        "example": "created_at"
                                      },
                                      "operator": {
                                        "type": "string",
                                        "enum": [
                                          "=",
                                          "!=",
                                          "IN",
                                          "NIN",
                                          "<",
                                          ">",
                                          "~",
                                          "!~",
                                          "^",
                                          "$"
                                        ],
                                        "description": "The accepted operators you can use to define how you want to search for the value.",
                                        "example": ">"
                                      },
                                      "value": {
                                        "oneOf": [
                                          {
                                            "type": "string"
                                          },
                                          {
                                            "type": "integer"
                                          },
                                          {
                                            "type": "array",
                                            "items": {
                                              "type": "string"
                                            }
                                          },
                                          {
                                            "type": "array",
                                            "items": {
                                              "type": "integer"
                                            }
                                          }
                                        ],
                                        "description": "The value that you want to search on.",
                                        "example": "73732934",
                                        "nullable": true
                                      }
                                    }
                                  }
                                ]
                              }
                            }
                          }
                        },
                        {
                          "title": "Single Filter Search Request",
                          "description": "Search using Intercoms Search APIs with a single filter.",
                          "type": "object",
                          "properties": {
                            "field": {
                              "type": "string",
                              "description": "The accepted field that you want to search on.",
                              "example": "created_at"
                            },
                            "operator": {
                              "type": "string",
                              "enum": [
                                "=",
                                "!=",
                                "IN",
                                "NIN",
                                "<",
                                ">",
                                "~",
                                "!~",
                                "^",
                                "$"
                              ],
                              "description": "The accepted operators you can use to define how you want to search for the value.",
                              "example": ">"
                            },
                            "value": {
                              "oneOf": [
                                {
                                  "type": "string"
                                },
                                {
                                  "type": "integer"
                                },
                                {
                                  "type": "array",
                                  "items": {
                                    "type": "string"
                                  }
                                },
                                {
                                  "type": "array",
                                  "items": {
                                    "type": "integer"
                                  }
                                }
                              ],
                              "description": "The value that you want to search on.",
                              "example": "73732934",
                              "nullable": true
                            }
                          }
                        }
                      ]
                    }
                  }
                }
              }
            ]
          },
          "pagination": {
            "title": "Pagination: Starting After",
            "type": "object",
            "nullable": true,
            "properties": {
              "per_page": {
                "type": "integer",
                "description": "The number of results to fetch per page.",
                "example": 2
              },
              "starting_after": {
                "type": "string",
                "description": "The cursor to use in the next request to get the next page of results.",
                "nullable": true,
                "example": "your-cursor-from-response"
              }
            }
          }
        },
        "required": [
          "query"
        ]
      },
      "examples": {
        "successful": {
          "summary": "successful",
          "value": {
            "query": {
              "operator": "AND",
              "value": [
                {
                  "field": "created_at",
                  "operator": ">",
                  "value": "1306054154"
                }
              ]
            },
            "pagination": {
              "per_page": 5
            }
          }
        }
      }
    }
  }
}
