http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "away_mode_enabled",
          "away_mode_reassign"
        ],
        "properties": {
          "away_mode_enabled": {
            "type": "boolean",
            "description": "Set to \"true\" to change the status of the admin to away.",
            "example": true,
            "default": true
          },
          "away_mode_reassign": {
            "type": "boolean",
            "description": "Set to \"true\" to assign any new conversation replies to your default inbox.",
            "example": false,
            "default": false
          }
        }
      },
      "examples": {
        "successful_response": {
          "summary": "Successful response",
          "value": {
            "away_mode_enabled": true,
            "away_mode_reassign": true
          }
        },
        "admin_not_found": {
          "summary": "Admin not found",
          "value": {
            "away_mode_enabled": true,
            "away_mode_reassign": true
          }
        },
        "unauthorized": {
          "summary": "Unauthorized",
          "value": {
            "away_mode_enabled": true,
            "away_mode_reassign": true
          }
        }
      }
    }
  }
}
