http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "You can update a Ticket",
        "type": "object",
        "title": "Update Ticket Request Payload",
        "properties": {
          "ticket_attributes": {
            "type": "object",
            "description": "The attributes set on the ticket.",
            "example": {
              "_default_title_": "example",
              "_default_description_": "having a problem"
            }
          },
          "state": {
            "type": "string",
            "enum": [
              "in_progress",
              "waiting_on_customer",
              "resolved"
            ],
            "description": "The state of the ticket.",
            "example": "submitted"
          },
          "is_shared": {
            "type": "boolean",
            "description": "Specify whether the ticket is visible to users.",
            "example": true
          },
          "assignment": {
            "type": "object",
            "properties": {
              "admin_id": {
                "type": "string",
                "description": "The ID of the admin performing the action.",
                "example": "123"
              },
              "assignee_id": {
                "type": "string",
                "description": "The ID of the admin or team to which the ticket is assigned. Set this 0 to unassign it.",
                "example": "123"
              }
            }
          }
        }
      },
      "examples": {
        "successful_response": {
          "summary": "Successful response",
          "value": {
            "ticket_attributes": {
              "_default_title_": "example",
              "_default_description_": "there is a problem"
            },
            "state": "in_progress",
            "assignment": {
              "admin_id": "991267719",
              "assignee_id": "991267721"
            },
            "open": true,
            "snoozed_until": 1673609604
          }
        },
        "admin_not_found": {
          "summary": "Admin not found",
          "value": {
            "ticket_attributes": {
              "_default_title_": "example",
              "_default_description_": "there is a problem"
            },
            "state": "in_progress",
            "assignment": {
              "admin_id": "123",
              "assignee_id": "991267729"
            }
          }
        },
        "assignee_not_found": {
          "summary": "Assignee not found",
          "value": {
            "ticket_attributes": {
              "_default_title_": "example",
              "_default_description_": "there is a problem"
            },
            "state": "in_progress",
            "assignment": {
              "admin_id": "991267735",
              "assignee_id": "456"
            }
          }
        }
      }
    }
  }
}
