http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Update an existing visitor.",
        "type": "object",
        "title": "Update Visitor Request Payload",
        "properties": {
          "id": {
            "type": "string",
            "description": "A unique identified for the visitor which is given by Intercom.",
            "example": "8a88a590-e"
          },
          "user_id": {
            "type": "string",
            "description": "A unique identified for the visitor which is given by you.",
            "example": "123"
          },
          "name": {
            "type": "string",
            "description": "The visitor's name.",
            "example": "Christian Bale"
          },
          "custom_attributes": {
            "type": "object",
            "description": "The custom attributes which are set for the visitor.",
            "additionalProperties": {
              "type": "string"
            },
            "example": {
              "paid_subscriber": true,
              "monthly_spend": 155.5,
              "team_mates": 9
            }
          }
        },
        "anyOf": [
          {
            "required": [
              "id"
            ]
          },
          {
            "required": [
              "user_id"
            ]
          }
        ]
      },
      "examples": {
        "successful": {
          "summary": "successful",
          "value": {
            "id": "6657abf76abd0164c24b0df9",
            "name": "Gareth Bale"
          }
        },
        "visitor_not_found": {
          "summary": "visitor Not Found",
          "value": {
            "user_id": "fail",
            "name": "Christian Fail"
          }
        }
      }
    }
  }
}
