http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "$schema": "https://json-schema.org/draft/2020-12/schema",
        "anyOf": [
          {
            "type": "object",
            "properties": {
              "platform": {
                "type": "string",
                "const": "external"
              },
              "url": {
                "type": "string"
              },
              "name": {
                "type": "string"
              },
              "start_at": {
                "description": "ISO 8601 Datetime. For example, 2022-10-19T03:27:13.673Z",
                "type": "string",
                "format": "date-time",
                "pattern": "^(?:(?:\\d\\d[2468][048]|\\d\\d[13579][26]|\\d\\d0[48]|[02468][048]00|[13579][26]00)-02-29|\\d{4}-(?:(?:0[13578]|1[02])-(?:0[1-9]|[12]\\d|3[01])|(?:0[469]|11)-(?:0[1-9]|[12]\\d|30)|(?:02)-(?:0[1-9]|1\\d|2[0-8])))T(?:(?:[01]\\d|2[0-3]):[0-5]\\d(?::[0-5]\\d(?:\\.\\d+)?)?(?:Z|([+-](?:[01]\\d|2[0-3]):[0-5]\\d)))$"
              },
              "duration_interval": {
                "type": "string"
              },
              "timezone": {
                "description": "IANA Timezone, e.g. America/New_York. https://en.wikipedia.org/wiki/List_of_tz_database_time_zones",
                "type": "string"
              },
              "geo_address_json": {
                "anyOf": [
                  {
                    "anyOf": [
                      {
                        "type": "object",
                        "properties": {
                          "type": {
                            "type": "string",
                            "const": "manual"
                          },
                          "address": {
                            "type": "string"
                          }
                        },
                        "required": [
                          "type",
                          "address"
                        ]
                      },
                      {
                        "type": "object",
                        "properties": {
                          "type": {
                            "type": "string",
                            "const": "google"
                          },
                          "place_id": {
                            "type": "string"
                          },
                          "description": {
                            "anyOf": [
                              {
                                "type": "string"
                              },
                              {
                                "type": "null"
                              }
                            ]
                          }
                        },
                        "required": [
                          "type",
                          "place_id"
                        ]
                      }
                    ]
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "host": {
                "anyOf": [
                  {
                    "type": "string"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "geo_longitude": {
                "anyOf": [
                  {
                    "type": "number"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "geo_latitude": {
                "anyOf": [
                  {
                    "type": "number"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "coordinate": {
                "anyOf": [
                  {
                    "type": "object",
                    "properties": {
                      "longitude": {
                        "type": "number"
                      },
                      "latitude": {
                        "type": "number"
                      }
                    },
                    "required": [
                      "longitude",
                      "latitude"
                    ]
                  },
                  {
                    "type": "null"
                  }
                ]
              }
            },
            "required": [
              "platform",
              "url",
              "name",
              "start_at",
              "duration_interval",
              "timezone"
            ]
          },
          {
            "type": "object",
            "properties": {
              "platform": {
                "type": "string",
                "const": "luma"
              },
              "event_api_id": {
                "type": "string"
              }
            },
            "required": [
              "platform",
              "event_api_id"
            ]
          }
        ]
      }
    }
  }
}
