http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "$schema": "https://json-schema.org/draft/2020-12/schema",
        "type": "object",
        "properties": {
          "tag": {
            "description": "Tag API ID (e.g., 'tag-123') or tag name",
            "type": "string"
          },
          "user_api_ids": {
            "description": "Array of user API IDs to apply the tag to",
            "type": "array",
            "items": {
              "type": "string"
            }
          },
          "emails": {
            "description": "Array of email addresses to apply the tag to",
            "type": "array",
            "items": {
              "type": "string"
            }
          }
        },
        "required": [
          "tag"
        ]
      }
    }
  }
}
