http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "$schema": "https://json-schema.org/draft/2020-12/schema",
        "allOf": [
          {
            "type": "object",
            "properties": {
              "event_api_id": {
                "description": "Event API ID (e.g., 'evt-123')",
                "type": "string"
              }
            },
            "required": [
              "event_api_id"
            ]
          },
          {
            "type": "object",
            "properties": {
              "name": {
                "type": "string"
              },
              "require_approval": {
                "type": "boolean"
              },
              "is_hidden": {
                "type": "boolean"
              },
              "description": {
                "anyOf": [
                  {
                    "type": "string"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "valid_start_at": {
                "anyOf": [
                  {
                    "description": "ISO 8601 Date. For example, 2025-09-01",
                    "type": "string"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "valid_end_at": {
                "anyOf": [
                  {
                    "description": "ISO 8601 Date. For example, 2025-09-01",
                    "type": "string"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "max_capacity": {
                "anyOf": [
                  {
                    "type": "number"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "type": {
                "type": "string",
                "enum": [
                  "free",
                  "paid"
                ]
              },
              "cents": {
                "anyOf": [
                  {
                    "type": "number"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "currency": {
                "anyOf": [
                  {
                    "type": "string"
                  },
                  {
                    "type": "null"
                  }
                ]
              },
              "is_flexible": {
                "type": "boolean"
              },
              "min_cents": {
                "anyOf": [
                  {
                    "type": "number"
                  },
                  {
                    "type": "null"
                  }
                ]
              }
            },
            "required": [
              "name",
              "type"
            ]
          }
        ]
      }
    }
  }
}
