http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "$schema": "https://json-schema.org/draft/2020-12/schema",
        "type": "object",
        "properties": {
          "infos": {
            "description": "Information about the people you want to import. You can include a name and email. Note that we will not set the name of a user once it has already been set.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "email": {
                  "type": "string"
                },
                "name": {
                  "anyOf": [
                    {
                      "type": "string"
                    },
                    {
                      "type": "null"
                    }
                  ]
                }
              },
              "required": [
                "email"
              ]
            }
          },
          "tag_api_ids": {
            "description": "The list of tags to apply to people once you have imported them into your calendar. This can be used to import new members with tags and to tag existing members.",
            "anyOf": [
              {
                "type": "array",
                "items": {
                  "type": "string"
                }
              },
              {
                "type": "null"
              }
            ]
          },
          "tag_names": {
            "description": "Note: we will not create tags that do not exist. If you want to use a new tag, create a tag before importing people.",
            "anyOf": [
              {
                "type": "array",
                "items": {
                  "type": "string"
                }
              },
              {
                "type": "null"
              }
            ]
          }
        },
        "required": [
          "infos"
        ]
      }
    }
  }
}
