http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "$schema": "https://json-schema.org/draft/2020-12/schema",
        "type": "object",
        "properties": {
          "event_api_id": {
            "type": "string"
          },
          "guests": {
            "description": "Information about the guests you want to add to the event. If a user already has a name, this will not overwrite their name.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "email": {
                  "type": "string"
                },
                "name": {
                  "anyOf": [
                    {
                      "type": "string"
                    },
                    {
                      "type": "null"
                    }
                  ]
                }
              },
              "required": [
                "email"
              ]
            }
          },
          "message": {
            "description": "Personalize the message that will be included in the invite. Max of 200 characters.",
            "anyOf": [
              {
                "type": "string",
                "maxLength": 200
              },
              {
                "type": "null"
              }
            ]
          }
        },
        "required": [
          "event_api_id",
          "guests"
        ]
      }
    }
  }
}
