http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "$schema": "https://json-schema.org/draft/2020-12/schema",
        "type": "object",
        "properties": {
          "event_api_id": {
            "type": "string"
          },
          "can_register_for_multiple_tickets": {
            "type": "boolean"
          },
          "cover_url": {
            "description": "Specify an image that has been uploaded to the Luma CDN. You can upload an image after getting an upload URL from the Luma API.",
            "type": "string",
            "format": "uri",
            "pattern": "^https:\\/\\/images\\.lumacdn\\.com\\/.*"
          },
          "coordinate": {
            "type": "object",
            "properties": {
              "longitude": {
                "type": "number"
              },
              "latitude": {
                "type": "number"
              }
            },
            "required": [
              "longitude",
              "latitude"
            ]
          },
          "description_md": {
            "description": "Luma stores rich text in a special format called Spark. We don't expose an API for that format, but we can convert Markdown to Spark for you. Some Spark features, like Luma event embeds, are not supported by Markdown.",
            "type": "string"
          },
          "end_at": {
            "description": "ISO 8601 Datetime. For example, 2022-10-19T03:27:13.673Z",
            "type": "string",
            "format": "date-time",
            "pattern": "^(?:(?:\\d\\d[2468][048]|\\d\\d[13579][26]|\\d\\d0[48]|[02468][048]00|[13579][26]00)-02-29|\\d{4}-(?:(?:0[13578]|1[02])-(?:0[1-9]|[12]\\d|3[01])|(?:0[469]|11)-(?:0[1-9]|[12]\\d|30)|(?:02)-(?:0[1-9]|1\\d|2[0-8])))T(?:(?:[01]\\d|2[0-3]):[0-5]\\d(?::[0-5]\\d(?:\\.\\d+)?)?(?:Z|([+-](?:[01]\\d|2[0-3]):[0-5]\\d)))$"
          },
          "geo_address_json": {
            "anyOf": [
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "manual"
                  },
                  "address": {
                    "type": "string"
                  }
                },
                "required": [
                  "type",
                  "address"
                ]
              },
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "google"
                  },
                  "place_id": {
                    "type": "string"
                  },
                  "description": {
                    "anyOf": [
                      {
                        "type": "string"
                      },
                      {
                        "type": "null"
                      }
                    ]
                  }
                },
                "required": [
                  "type",
                  "place_id"
                ]
              }
            ]
          },
          "max_capacity": {
            "description": "Once an event hits the max capacity, the event will be marked as sold out and registration will automatically be closed.\n\nIf `can_register_for_multiple_tickets` is `true`, each ticket will count towards the max capacity.",
            "anyOf": [
              {
                "type": "integer",
                "minimum": -9007199254740991,
                "maximum": 9007199254740991
              },
              {
                "type": "null"
              }
            ]
          },
          "meeting_url": {
            "type": "string"
          },
          "name": {
            "type": "string"
          },
          "name_requirement": {
            "description": "Luma collects the name of every guest as they register. Choose if you'd like to split this into two fields to encourage guests to enter their first and last name.",
            "type": "string",
            "enum": [
              "full-name",
              "first-last"
            ]
          },
          "phone_number_requirement": {
            "anyOf": [
              {
                "type": "string",
                "enum": [
                  "optional",
                  "required"
                ]
              },
              {
                "type": "null"
              }
            ]
          },
          "registration_questions": {
            "type": "array",
            "items": {
              "anyOf": [
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "agree-check"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "company"
                    },
                    "collect_job_title": {
                      "type": "boolean"
                    },
                    "job_title_label": {
                      "type": "string"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "dropdown"
                    },
                    "options": {
                      "maxItems": 250,
                      "type": "array",
                      "items": {
                        "type": "string"
                      }
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type",
                    "options"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "github"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "instagram"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "linkedin"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "long-text"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "multi-select"
                    },
                    "options": {
                      "maxItems": 250,
                      "type": "array",
                      "items": {
                        "type": "string"
                      }
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type",
                    "options"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "phone-number"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "telegram"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "twitter"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "text"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "url"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "label": {
                      "type": "string"
                    },
                    "required": {
                      "type": "boolean"
                    },
                    "question_type": {
                      "type": "string",
                      "const": "youtube"
                    }
                  },
                  "required": [
                    "id",
                    "label",
                    "required",
                    "question_type"
                  ]
                }
              ]
            }
          },
          "reminders_disabled": {
            "description": "By default, Luma will send reminders before the event. Turn off if you'd like more control over event communications.",
            "type": "boolean"
          },
          "show_guest_list": {
            "description": "When the guest list is shown, approved guests will be able to see who else is going to the event.",
            "type": "boolean"
          },
          "slug": {
            "description": "The URL slug for the event. If the slug is `aloha`, the URL will be `https://luma.com/aloha`. If the slug is not available, the event creation will fail.\n\nYou can use `/public/v1/entity/lookup` to check if a slug is available.",
            "type": "string",
            "minLength": 3,
            "maxLength": 50
          },
          "start_at": {
            "description": "ISO 8601 Datetime. For example, 2022-10-19T03:27:13.673Z",
            "type": "string",
            "format": "date-time",
            "pattern": "^(?:(?:\\d\\d[2468][048]|\\d\\d[13579][26]|\\d\\d0[48]|[02468][048]00|[13579][26]00)-02-29|\\d{4}-(?:(?:0[13578]|1[02])-(?:0[1-9]|[12]\\d|3[01])|(?:0[469]|11)-(?:0[1-9]|[12]\\d|30)|(?:02)-(?:0[1-9]|1\\d|2[0-8])))T(?:(?:[01]\\d|2[0-3]):[0-5]\\d(?::[0-5]\\d(?:\\.\\d+)?)?(?:Z|([+-](?:[01]\\d|2[0-3]):[0-5]\\d)))$"
          },
          "timezone": {
            "description": "IANA Timezone, e.g. America/New_York. https://en.wikipedia.org/wiki/List_of_tz_database_time_zones",
            "type": "string"
          },
          "tint_color": {
            "description": "A hex color like '#bb2dc7'. Alpha channels (#rgba, #rrggbbaa) are automatically stripped.",
            "type": "string"
          },
          "visibility": {
            "type": "string",
            "enum": [
              "public",
              "members-only",
              "private"
            ]
          }
        },
        "required": [
          "event_api_id"
        ]
      }
    }
  }
}
