http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "$schema": "https://json-schema.org/draft/2020-12/schema",
        "type": "object",
        "properties": {
          "guest": {
            "anyOf": [
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "email"
                  },
                  "email": {
                    "type": "string"
                  }
                },
                "required": [
                  "type",
                  "email"
                ]
              },
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "const": "api_id"
                  },
                  "api_id": {
                    "type": "string"
                  }
                },
                "required": [
                  "type",
                  "api_id"
                ]
              }
            ]
          },
          "event_api_id": {
            "type": "string"
          },
          "status": {
            "type": "string",
            "enum": [
              "declined",
              "approved"
            ]
          },
          "should_refund": {
            "description": "If you are declining a guest and they have paid, should we refund their payment?",
            "type": "boolean"
          }
        },
        "required": [
          "guest",
          "event_api_id",
          "status"
        ]
      }
    }
  }
}
