http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "email",
          "role"
        ],
        "properties": {
          "email": {
            "type": "string",
            "description": "Email ID of the user.",
            "example": "someone@domain.com"
          },
          "role": {
            "type": "string",
            "description": "Role of the project member.",
            "enum": [
              "owner",
              "editor",
              "viewer",
              "commentator",
              "coowner"
            ],
            "example": "viewer"
          }
        }
      }
    }
  },
  "required": true
}
