http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "boardIds": {
            "maxItems": 50,
            "minItems": 1,
            "type": "array",
            "description": "List of board IDs to be exported.",
            "example": "o9J_kzlUDmo=",
            "items": {
              "type": "string",
              "description": "List of board IDs to be exported.",
              "example": "o9J_kzlUDmo="
            }
          },
          "boardFormat": {
            "type": "string",
            "enum": [
              "SVG",
              "HTML",
              "PDF"
            ],
            "default": "SVG",
            "description": "Specifies the format of the file to which the board will be exported. Supported formats include SVG (default), HTML, and PDF.",
            "example": "SVG"
          }
        },
        "description": "List of board IDs to be exported."
      }
    }
  },
  "required": true
}
