{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Contains the result data.",
        "properties": {
          "data": {
            "type": "object",
            "properties": {
              "items": {
                "type": "array",
                "description": "Unique identifier (ID) of the item.",
                "items": {
                  "type": "string"
                },
                "example": [
                  "3458764517517852417",
                  "3458764517517852418"
                ]
              }
            }
          }
        }
      }
    }
  },
  "required": true
}
