http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "runner": {
            "oneOf": [
              {
                "type": "object",
                "title": "RunnerSidecarPostBody",
                "properties": {
                  "runner_type": {
                    "description": "sidecar -- The runner is backed by an external sidecar that polls for invocations.\nrunbook -- The runner communicates directly with a runbook instance.\n",
                    "type": "string",
                    "enum": [
                      "sidecar",
                      "runbook"
                    ],
                    "example": "runbook"
                  },
                  "name": {
                    "type": "string",
                    "maxLength": 255,
                    "example": "us-west-2 prod runner"
                  },
                  "description": {
                    "type": "string",
                    "maxLength": 1024,
                    "example": "us-west-2 runner provisioned in the production environment by the SRE team"
                  },
                  "teams": {
                    "type": "array",
                    "description": "The list of teams associated with the Runner",
                    "items": {
                      "allOf": [
                        {
                          "allOf": [
                            {
                              "type": "object",
                              "properties": {
                                "id": {
                                  "type": "string",
                                  "readOnly": true
                                },
                                "summary": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                },
                                "type": {
                                  "type": "string",
                                  "readOnly": true,
                                  "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                },
                                "self": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "format": "url",
                                  "description": "the API show URL at which the object is accessible"
                                },
                                "html_url": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "format": "url",
                                  "description": "a URL at which the entity is uniquely displayed in the Web app"
                                }
                              }
                            },
                            {
                              "type": "object",
                              "required": [
                                "type",
                                "id"
                              ]
                            }
                          ]
                        },
                        {
                          "type": "object",
                          "properties": {
                            "type": {
                              "type": "string",
                              "enum": [
                                "team_reference"
                              ]
                            }
                          }
                        }
                      ]
                    }
                  }
                },
                "required": [
                  "runner_type",
                  "name",
                  "description"
                ]
              },
              {
                "type": "object",
                "title": "RunnerRunbookPostBody",
                "properties": {
                  "runner_type": {
                    "description": "sidecar -- The runner is backed by an external sidecar that polls for invocations.\nrunbook -- The runner communicates directly with a runbook instance.\n",
                    "type": "string",
                    "enum": [
                      "sidecar",
                      "runbook"
                    ],
                    "example": "runbook"
                  },
                  "name": {
                    "type": "string",
                    "maxLength": 255,
                    "example": "us-west-2 prod runner"
