        ]
      },
      "examples": {
        "request": {
          "value": {
            "runner": {
              "name": "us-west-2 prod sidecar runner",
              "description": "us-west-2 prod sidecar runner provisioned by SRE",
              "runner_type": "sidecar",
              "teams": [
                {
                  "id": "PQ9K7I8",
                  "type": "team_reference"
                }
              ]
