http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "orchestration": {
            "type": "object",
            "properties": {
              "id": {
                "type": "string",
                "description": "ID of the Orchestration.",
                "readOnly": true
              },
              "self": {
                "type": "string",
                "format": "url",
                "description": "The API show URL at which the object is accessible",
                "readOnly": true
              },
              "name": {
                "type": "string",
                "description": "Name of the Orchestration."
              },
              "description": {
                "type": "string",
                "description": "A description of this Orchestration's purpose."
              },
              "team": {
                "type": "object",
                "description": "Reference to the team that owns the Orchestration. If none is specified, only admins have access.",
                "properties": {
                  "id": {
                    "type": "string"
                  },
                  "type": {
                    "type": "string",
                    "description": "A string that determines the schema of the object",
                    "readOnly": true
                  },
                  "self": {
                    "type": "string",
                    "format": "url",
                    "description": "The API show URL at which the object is accessible",
                    "readOnly": true
                  }
                }
              },
              "integrations": {
                "type": "array",
                "items": {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string",
                      "description": "ID of the Integration.",
                      "readOnly": true
                    },
                    "label": {
                      "type": "string",
                      "description": "Name of the Integration."
                    },
                    "parameters": {
                      "type": "object",
                      "readOnly": true,
                      "properties": {
                        "routing_key": {
                          "type": "string",
                          "description": "Routing Key used to send Events to this Orchestration",
                          "readOnly": true
                        },
                        "type": {
                          "type": "string",
                          "default": "global",
                          "readOnly": true
                        }
                      }
                    }
                  }
                },
                "readOnly": true
              },
              "routes": {
                "type": "integer",
                "description": "Number of different Service Orchestration being routed to",
                "readOnly": true
              },
              "created_at": {
                "type": "string",
                "format": "date-time",
                "description": "The date the Orchestration was created at.",
                "readOnly": true
              },
              "created_by": {
                "type": "object",
                "description": "Reference to the user that has created the Orchestration.",
                "properties": {
                  "id": {
                    "type": "string",
                    "readOnly": true
                  },
                  "type": {
                    "type": "string",
                    "description": "A string that determines the schema of the object",
                    "readOnly": true
                  },
                  "self": {
                    "type": "string",
                    "format": "url",
                    "description": "The API show URL at which the object is accessible",
                    "readOnly": true
                  }
                },
                "readOnly": true
              },
              "updated_at": {
                "type": "string",
                "format": "date-time",
                "description": "The date the Orchestration was last updated.",
                "readOnly": true
              },
              "updated_by": {
                "type": "object",
                "description": "Reference to the user that has updated the Orchestration last.",
                "properties": {
                  "id": {
                    "type": "string",
                    "readOnly": true
                  },
                  "type": {
                    "type": "string",
                    "description": "A string that determines the schema of the object",
                    "readOnly": true
                  },
                  "self": {
                    "type": "string",
                    "format": "url",
                    "description": "The API show URL at which the object is accessible",
                    "readOnly": true
                  }
                },
                "readOnly": true
              },
              "version": {
                "type": "string",
                "description": "Version of the Orchestration.",
                "readOnly": true
              }
            }
          }
        },
        "required": [
          "orchestration"
        ]
      },
      "examples": {
        "create_orchestration": {
          "summary": "Example: Create Orchestration",
          "value": {
            "orchestration": {
              "name": "New Orchestration",
              "description": "This is a newly created orchestration",
              "team": {
                "id": "PXD0WR8"
              }
            }
          }
        }
      }
    }
  }
}
