http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "requester_id": {
            "type": "string",
            "description": "The user id of the requester."
          },
          "message": {
            "type": "string",
            "description": "The message sent with the responder request."
          },
          "responder_request_targets": {
            "description": "The array of targets the responder request is sent to.",
            "items": {
              "type": "object",
              "properties": {
                "type": {
                  "type": "string",
                  "description": "The type of target (either a user or an escalation policy)"
                },
                "id": {
                  "type": "string",
                  "description": "The id of the user or escalation policy"
                },
                "summary": {
                  "type": "string"
                },
                "incident_responders": {
                  "type": "array",
                  "description": "An array of responders associated with the specified incident",
                  "items": {
                    "type": "object",
                    "properties": {
                      "state": {
                        "type": "string",
                        "description": "The status of the responder being added to the incident",
                        "example": "pending"
                      },
                      "user": {
                        "allOf": [
                          {
                            "allOf": [
                              {
                                "type": "object",
                                "properties": {
                                  "id": {
                                    "type": "string",
                                    "readOnly": true
                                  },
                                  "summary": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                  },
                                  "type": {
                                    "type": "string",
                                    "readOnly": true,
                                    "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                  },
                                  "self": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "format": "url",
                                    "description": "the API show URL at which the object is accessible"
                                  },
                                  "html_url": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "format": "url",
                                    "description": "a URL at which the entity is uniquely displayed in the Web app"
                                  }
                                }
                              },
                              {
                                "type": "object",
                                "required": [
                                  "type",
                                  "id"
                                ]
                              }
                            ]
                          },
                          {
                            "type": "object",
                            "properties": {
                              "type": {
                                "type": "string",
                                "enum": [
                                  "user_reference"
                                ]
                              }
                            }
                          }
                        ]
                      },
                      "incident": {
                        "allOf": [
                          {
                            "allOf": [
                              {
                                "type": "object",
                                "properties": {
                                  "id": {
                                    "type": "string",
                                    "readOnly": true
                                  },
                                  "summary": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                  },
                                  "type": {
                                    "type": "string",
                                    "readOnly": true,
                                    "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                  },
                                  "self": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "format": "url",
                                    "description": "the API show URL at which the object is accessible"
                                  },
                                  "html_url": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "format": "url",
                                    "description": "a URL at which the entity is uniquely displayed in the Web app"
                                  }
                                }
                              },
                              {
                                "type": "object",
                                "required": [
                                  "type",
                                  "id"
                                ]
                              }
                            ]
                          },
                          {
                            "type": "object",
                            "properties": {
                              "type": {
                                "type": "string",
                                "enum": [
                                  "incident_reference"
                                ]
                              }
                            }
                          }
                        ]
                      },
                      "updated_at": {
                        "type": "string"
                      },
                      "message": {
                        "type": "string",
                        "description": "The message sent with the responder request"
                      },
                      "requester": {
                        "allOf": [
                          {
                            "allOf": [
                              {
                                "type": "object",
                                "properties": {
                                  "id": {
                                    "type": "string",
                                    "readOnly": true
                                  },
                                  "summary": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                  },
                                  "type": {
                                    "type": "string",
                                    "readOnly": true,
                                    "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                  },
                                  "self": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "format": "url",
                                    "description": "the API show URL at which the object is accessible"
                                  },
                                  "html_url": {
                                    "type": "string",
                                    "nullable": true,
                                    "readOnly": true,
                                    "format": "url",
                                    "description": "a URL at which the entity is uniquely displayed in the Web app"
                                  }
                                }
                              },
                              {
                                "type": "object",
                                "required": [
                                  "type",
                                  "id"
                                ]
                              }
                            ]
                          },
                          {
                            "type": "object",
                            "properties": {
                              "type": {
                                "type": "string",
                                "enum": [
                                  "user_reference"
                                ]
                              }
                            }
                          }
                        ]
                      },
                      "requested_at": {
                        "type": "string"
                      }
                    }
                  }
                }
              }
            }
          }
        },
        "required": [
          "requester_id",
          "message",
          "responder_request_targets"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "requester_id": "PL1JMK5",
            "message": "Please help with issue - join bridge at +1(234)-567-8910",
            "responder_request_targets": [
              {
                "responder_request_target": {
                  "id": "PJ25ZYX",
                  "type": "user_reference"
                }
              }
            ]
          }
        }
      }
    }
  }
}
