http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "field": {
            "type": "object",
            "description": "Details of the custom field to be created.",
            "properties": {
              "name": {
                "type": "string",
                "description": "The name of the custom field.",
                "maxLength": 50
              },
              "display_name": {
                "type": "string",
                "description": "The display name of the Incident Type.",
                "maxLength": 50
              },
              "data_type": {
                "type": "string",
                "description": "The data type of the custom field."
              },
              "field_type": {
                "type": "string",
                "description": "The field type of the custom field.",
                "enum": [
                  "single_value",
                  "single_value_fixed",
                  "multi_value",
                  "multi_value_fixed"
                ]
              },
              "description": {
                "type": "string",
                "description": "The description of the custom field.",
                "maxLength": 1000
              },
              "enabled": {
                "type": "boolean",
                "description": "Whether the custom field is enabled."
              },
              "default_value": {
                "type": "string",
                "description": "The default value of the custom field."
              },
              "field_options": {
                "type": "array",
                "items": {
                  "allOf": [
                    {
                      "type": "object",
                      "properties": {
                        "data": {
                          "oneOf": [
                            {
                              "type": "object",
                              "properties": {
                                "data_type": {
                                  "type": "string",
                                  "description": "The kind of data represented by this option. Must match the Field's `data_type`.",
                                  "enum": [
                                    "string"
                                  ]
                                },
                                "value": {
                                  "type": "string",
                                  "maxLength": 200
                                }
                              },
                              "required": [
                                "data_type",
                                "value"
                              ]
                            }
                          ],
                          "discriminator": {
                            "propertyName": "data_type",
                            "mapping": {
                              "string": "#/paths/~1incidents~1custom_fields/get/responses/200/content/application~1json/schema/allOf/0/properties/fields/items/allOf/0/properties/field_options/items/allOf/0/properties/data/oneOf/0"
                            }
                          }
                        },
                        "id": {
                          "type": "string",
                          "readOnly": true,
                          "description": "The ID of the resource."
                        },
                        "type": {
                          "type": "string",
                          "enum": [
                            "field_option"
                          ],
                          "readOnly": true
                        },
                        "updated_at": {
                          "type": "string",
                          "format": "date-time",
                          "description": "The date/time the object was last updated.",
                          "readOnly": true
                        },
                        "created_at": {
                          "type": "string",
                          "format": "date-time",
                          "description": "The date/time the object was created at.",
                          "readOnly": true
                        }
                      },
                      "required": [
                        "id",
                        "type",
                        "created_at",
                        "updated_at"
                      ],
                      "description": ""
                    },
                    {
                      "type": "object",
                      "required": [
                        "id",
                        "data",
                        "created_at",
                        "updated_at"
                      ]
                    }
                  ]
                },
                "description": "The options for the custom field. Can only be applied to fields with a `field_type` of `single_value_fixed` or `multi_value_fixed`. When creating a fixed-value custom field, this property is required, as the field must be created with at least one field option."
              }
            },
            "required": [
              "name",
              "display_name",
              "data_type",
              "field_type"
            ]
          }
        },
        "required": [
          "field"
        ]
      },
      "examples": {
        "simple_example": {
          "summary": "Request Example",
          "value": {
            "field": {
              "name": "custom_field_1",
              "display_name": "Custom Field 1",
              "data_type": "string",
              "field_type": "single_value"
            }
          }
        },
        "example_with_fixed_value_field": {
          "summary": "Example with Fixed Value Field",
          "value": {
            "field": {
              "name": "development_environment",
              "display_name": "Development Environment",
              "description": "The environment that the issue occurred in",
              "data_type": "string",
              "field_type": "single_value_fixed",
              "field_options": [
                {
                  "data": {
                    "data_type": "string",
                    "value": "staging"
                  }
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "production"
                  }
                }
              ]
            }
          }
        }
      }
    }
  }
}
