http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "",
        "type": "object",
        "properties": {
          "overrides": {
            "type": "array",
            "items": {
              "allOf": [
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string",
                      "readOnly": true
                    },
                    "summary": {
                      "type": "string",
                      "nullable": true,
                      "readOnly": true,
                      "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                    },
                    "type": {
                      "type": "string",
                      "readOnly": true,
                      "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                    },
                    "self": {
                      "type": "string",
                      "nullable": true,
                      "readOnly": true,
                      "format": "url",
                      "description": "the API show URL at which the object is accessible"
                    },
                    "html_url": {
                      "type": "string",
                      "nullable": true,
                      "readOnly": true,
                      "format": "url",
                      "description": "a URL at which the entity is uniquely displayed in the Web app"
                    }
                  }
                },
                {
                  "type": "object",
                  "properties": {
                    "id": {
                      "type": "string",
                      "readOnly": true
                    },
                    "start": {
                      "description": "The start date and time for the override.",
                      "type": "string",
                      "format": "date-time"
                    },
                    "end": {
                      "description": "The end date and time for the override.",
                      "type": "string",
                      "format": "date-time"
                    },
                    "user": {
                      "allOf": [
                        {
                          "allOf": [
                            {
                              "type": "object",
                              "properties": {
                                "id": {
                                  "type": "string",
                                  "readOnly": true
                                },
                                "summary": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                },
                                "type": {
                                  "type": "string",
                                  "readOnly": true,
                                  "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                },
                                "self": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "format": "url",
                                  "description": "the API show URL at which the object is accessible"
                                },
                                "html_url": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "format": "url",
                                  "description": "a URL at which the entity is uniquely displayed in the Web app"
                                }
                              }
                            },
                            {
                              "type": "object",
                              "required": [
                                "type",
                                "id"
                              ]
                            }
                          ]
                        },
                        {
                          "type": "object",
                          "properties": {
                            "type": {
                              "type": "string",
                              "enum": [
                                "user_reference"
                              ]
                            }
                          }
                        }
                      ]
                    }
                  },
                  "required": [
                    "start",
                    "end",
                    "user"
                  ],
                  "example": {
                    "start": "2012-07-01T00:00:00-04:00",
                    "end": "2012-07-02T00:00:00-04:00",
                    "user": {
                      "id": "PEYSGVF",
                      "type": "user_reference"
                    }
                  }
                }
              ]
            }
          }
        }
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "overrides": [
              {
                "start": "2012-07-01T00:00:00-04:00",
                "end": "2012-07-02T00:00:00-04:00",
                "user": {
                  "id": "PEYSGVA",
                  "type": "user_reference"
                },
                "time_zone": "UTC"
              },
              {
                "start": "2012-07-03T00:00:00-04:00",
                "end": "2012-07-04T00:00:00-04:00",
                "user": {
                  "id": "PEYSGVF",
                  "type": "user_reference"
                },
                "time_zone": "UTC"
              }
            ]
          }
        }
      }
    }
  },
  "description": "The overrides to be created",
  "required": true
}
