http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "field": {
            "type": "object",
            "description": "Details of the custom field to be created.",
            "properties": {
              "data_type": {
                "type": "string",
                "description": "The kind of data the custom field is allowed to contain.",
                "enum": [
                  "boolean",
                  "integer",
                  "float",
                  "string",
                  "datetime",
                  "url"
                ]
              },
              "description": {
                "type": "string",
                "nullable": true,
                "description": "A description of the data this field contains.",
                "maxLength": 1000
              },
              "display_name": {
                "type": "string",
                "description": "The human-readable name of the field. This must be unique across an account.",
                "maxLength": 50
              },
              "enabled": {
                "type": "boolean",
                "description": "Whether the field is enabled.",
                "enum": [
                  true,
                  false
                ]
              },
              "field_options": {
                "type": "array",
                "items": {
                  "type": "object",
                  "description": "An option for a custom field. Can only be applied to fields with a `field_type` of `single_value_fixed` or `multi_value_fixed`.",
                  "properties": {
                    "data": {
                      "type": "object",
                      "description": "The data content of the field option.",
                      "properties": {
                        "data_type": {
                          "type": "string",
                          "description": "The kind of data represented by this option. Must match the Field's `data_type`.",
                          "enum": [
                            "string"
                          ]
                        },
                        "value": {
                          "type": "string",
                          "description": "The value of the field option. Must be unique within the field.",
                          "maxLength": 200
                        }
                      },
                      "required": [
                        "data_type",
                        "value"
                      ]
                    }
                  },
                  "required": [
                    "data"
                  ]
                }
              },
              "field_type": {
                "type": "string",
                "description": "The type of data this field contains. In combination with the `data_type` field.",
                "enum": [
                  "single_value",
                  "single_value_fixed",
                  "multi_value",
                  "multi_value_fixed"
                ]
              },
              "name": {
                "type": "string",
                "title": "Field Name",
                "description": "The name of the field. May include ASCII characters, specifically lowercase letters, digits, and underescores. The `name` for a Field must be unique and cannot be changed once created.",
                "maxLength": 50
              }
            },
            "required": [
              "data_type",
              "display_name",
              "field_type",
              "name"
            ]
          }
        },
        "required": [
          "field"
        ]
      },
      "examples": {
        "request1": {
          "summary": "Example: With field_options and single-value",
          "value": {
            "field": {
              "data_type": "string",
              "description": "The environment that the service operates in",
              "display_name": "Environment",
              "enabled": true,
              "field_options": [
                {
                  "data": {
                    "data_type": "string",
                    "value": "production"
                  }
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "staging"
                  }
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "development"
                  }
                }
              ],
              "field_type": "single_value_fixed",
              "name": "environment"
            }
          }
        },
        "request2": {
          "summary": "Example: With field_options and multi-value",
          "value": {
            "field": {
              "data_type": "string",
              "description": "The regions where this service is deployed",
              "display_name": "Supported Regions",
              "enabled": true,
              "field_options": [
                {
                  "data": {
                    "data_type": "string",
                    "value": "us-east-1"
                  }
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "us-west-1"
                  }
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "eu-west-1"
                  }
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "ap-southeast-1"
                  }
                }
              ],
              "field_type": "multi_value_fixed",
              "name": "supported_regions"
            }
          }
        },
        "request3": {
          "summary": "Example: Without field_options",
          "value": {
            "field": {
              "data_type": "string",
              "description": "The team that owns this service",
              "display_name": "Team Owner",
              "enabled": true,
              "field_type": "single_value",
              "name": "team_owner"
            }
          }
        }
      }
    }
  }
}
