http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "subscription": {
            "type": "object",
            "title": "StatusPageSubscriptionRequest",
            "description": "Request schema for creating a StatusPageSubscription.",
            "properties": {
              "channel": {
                "description": "The channel of the Subscription.",
                "enum": [
                  "webhook",
                  "email"
                ],
                "nullable": false,
                "title": "SubscriptionChannel",
                "type": "string"
              },
              "contact": {
                "description": "The subscriber's contact - email address or webhook URL.",
                "type": "string",
                "nullable": false
              },
              "status_page": {
                "description": "Status Page",
                "nullable": false,
                "properties": {
                  "id": {
                    "description": "The id of the status page.",
                    "type": "string"
                  },
                  "type": {
                    "description": "A string that determines the schema of the object.",
                    "type": "string"
                  }
                },
                "required": [
                  "id"
                ],
                "type": "object"
              },
              "subscribable_object": {
                "type": "object",
                "title": "SubscribableObject",
                "description": "The subscribed entity for a given subscription.",
                "properties": {
                  "id": {
                    "description": "The ID of the subscribed entity for a given subscription.",
                    "type": "string",
                    "nullable": false
                  },
                  "type": {
                    "description": "The type of the subscribed entity for a given subscription.",
                    "enum": [
                      "status_page",
                      "status_page_service",
                      "status_page_post"
                    ],
                    "type": "string",
                    "nullable": false
                  }
                }
              },
              "type": {
                "description": "A string that determines the schema of the object.",
                "type": "string"
              }
            },
            "required": [
              "channel",
              "contact",
              "subscribable_object",
              "status_page",
              "type"
            ]
          }
        },
        "required": [
          "subscription"
        ]
      },
      "example": {
        "subscription": {
          "channel": "email",
          "contact": "joe@email.example",
          "status_page": {
            "id": "PIJ90N7",
            "type": "status_page"
          },
          "subscribable_object": {
            "id": "PSX4LJI",
            "type": "status_page_service"
          },
          "type": "status_page_subscription"
        }
      }
    }
  }
}
