http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "limit": {
            "type": "integer",
            "description": "Number of results to include in each batch.\nLimits between 1 to 1000 are accepted.",
            "example": 20,
            "minimum": 0,
            "exclusiveMinimum": true,
            "maximum": 1000,
            "exclusiveMaximum": false
          },
          "order": {
            "type": "string",
            "description": "The order in which to display the results; asc for ascending, desc for descending. Defaults to `desc`.",
            "enum": [
              "asc",
              "desc"
            ]
          },
          "order_by": {
            "type": "string",
            "description": "The column to use for ordering the results.",
            "enum": [
              "requested_at"
            ]
          },
          "time_zone": {
            "type": "string",
            "description": "The time zone to use for the results.",
            "example": "Etc/UTC"
          }
        }
      },
      "examples": {
        "Example Request": {
          "value": {
            "limit": 20,
            "order": "desc",
            "order_by": "requested_at",
            "time_zone": "America/Los_Angeles"
          }
        }
      }
    }
  },
  "description": "Parameters to apply to the dataset."
}
