http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "addon": {
            "allOf": [
              {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "readOnly": true
                  },
                  "summary": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                  },
                  "type": {
                    "type": "string",
                    "readOnly": true,
                    "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                  },
                  "self": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "the API show URL at which the object is accessible"
                  },
                  "html_url": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "a URL at which the entity is uniquely displayed in the Web app"
                  }
                }
              },
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "description": "The type of Add-on.",
                    "enum": [
                      "full_page_addon",
                      "incident_show_addon"
                    ]
                  },
                  "name": {
                    "type": "string",
                    "description": "The name of the Add-on.",
                    "maxLength": 100
                  },
                  "src": {
                    "type": "string",
                    "format": "url",
                    "description": "The source URL to display in a frame in the PagerDuty UI. HTTPS is required."
                  }
                },
                "required": [
                  "type",
                  "name",
                  "src"
                ],
                "example": {
                  "type": "full_page_addon",
                  "name": "Internal Status Page",
                  "src": "https://intranet.example.com/status"
                }
              }
            ]
          }
        },
        "required": [
          "addon"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "addon": {
              "type": "full_page_addon",
              "name": "Internal Status Page",
              "src": "https://intranet.example.com/status"
            }
          }
        }
      }
    }
  },
  "description": "The Add-on to be installed."
}
