http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "incident": {
            "type": "object",
            "description": "The parameters of the incident to update.",
            "properties": {
              "type": {
                "type": "string",
                "description": "The incident type.",
                "enum": [
                  "incident",
                  "incident_reference"
                ]
              },
              "status": {
                "type": "string",
                "description": "The new status of the incident. If the incident is currently resolved, setting the status to \"triggered\" or \"acknowledged\" will reopen it. When reopening an incident to the \"triggered\" status, it will be assigned based on the assignees or escalation_policy fields in the request, otherwise it will be assigned to the current Escalation Policy. When reopening an incident to the \"acknowledged\" status, it will be assigned to the current user.",
                "enum": [
                  "resolved",
                  "acknowledged",
                  "triggered"
                ]
              },
              "priority": {
                "description": "The priority of the incident. Can be provided as a priority object or a string matching a priority name. If a string is provided, the highest priority with a matching name will be used.",
                "oneOf": [
                  {
                    "type": "object",
                    "properties": {
                      "id": {
                        "type": "string",
                        "description": "The ID of the priority."
                      },
                      "name": {
                        "type": "string",
                        "description": "The user-provided short name of the priority."
                      },
                      "type": {
                        "type": "string",
                        "description": "The type of the reference.",
                        "enum": [
                          "priority",
                          "priority_reference"
                        ]
                      }
                    },
                    "required": [
                      "id",
                      "type"
                    ]
                  },
                  {
                    "type": "string",
                    "description": "A string matching the name of a priority. If provided, the highest priority with a matching name will be used."
                  }
                ]
              },
              "resolution": {
                "type": "string",
                "description": "The resolution for this incident. This field is used only when setting the incident status to resolved.\nThe value provided here is added to the incident\u2019s 'Resolve' log entry as a note and will not be displayed directly in the UI.\n"
              },
              "title": {
                "type": "string",
                "description": "The new title of the incident."
              },
              "escalation_level": {
                "type": "integer",
                "description": "Escalate the incident to this level in the escalation policy."
              },
              "assignments": {
                "type": "array",
                "description": "Assign the incident to these assignees.",
                "items": {
                  "properties": {
                    "assignee": {
                      "allOf": [
                        {
                          "allOf": [
                            {
                              "type": "object",
                              "properties": {
                                "id": {
                                  "type": "string",
                                  "readOnly": true
                                },
                                "summary": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                },
                                "type": {
                                  "type": "string",
                                  "readOnly": true,
                                  "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                },
                                "self": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "format": "url",
                                  "description": "the API show URL at which the object is accessible"
                                },
                                "html_url": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "format": "url",
                                  "description": "a URL at which the entity is uniquely displayed in the Web app"
                                }
                              }
                            },
                            {
                              "type": "object",
                              "required": [
                                "type",
                                "id"
                              ]
                            }
                          ]
                        },
                        {
                          "type": "object",
                          "properties": {
                            "type": {
                              "type": "string",
                              "enum": [
                                "user_reference"
                              ]
                            }
                          }
                        }
                      ]
                    }
                  }
                }
              },
              "incident_type": {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "readOnly": true
                  },
                  "name": {
                    "type": "string",
                    "description": "The name of the Incident Type."
                  }
                }
              },
              "escalation_policy": {
                "allOf": [
                  {
                    "allOf": [
                      {
                        "type": "object",
                        "properties": {
                          "id": {
                            "type": "string",
                            "readOnly": true
                          },
                          "summary": {
                            "type": "string",
                            "nullable": true,
                            "readOnly": true,
                            "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                          },
                          "type": {
                            "type": "string",
                            "readOnly": true,
                            "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                          },
                          "self": {
                            "type": "string",
                            "nullable": true,
                            "readOnly": true,
                            "format": "url",
                            "description": "the API show URL at which the object is accessible"
                          },
                          "html_url": {
                            "type": "string",
                            "nullable": true,
                            "readOnly": true,
                            "format": "url",
                            "description": "a URL at which the entity is uniquely displayed in the Web app"
                          }
                        }
                      },
                      {
                        "type": "object",
                        "required": [
                          "type",
                          "id"
                        ]
                      }
                    ]
                  },
                  {
                    "type": "object",
                    "properties": {
                      "type": {
                        "type": "string",
                        "enum": [
                          "escalation_policy_reference"
                        ]
                      }
                    }
                  }
                ]
              },
              "urgency": {
                "type": "string",
                "description": "The urgency of the incident.",
                "enum": [
                  "high",
                  "low"
                ]
              },
              "conference_bridge": {
                "type": "object",
                "properties": {
                  "conference_number": {
                    "type": "string",
                    "description": "The phone number of the conference call for the conference bridge. Phone numbers should be formatted like +1 415-555-1212,,,,1234#, where a comma (,) represents a one-second wait and pound (#) completes access code input."
                  },
                  "conference_url": {
                    "type": "string",
                    "format": "url",
                    "description": "An URL for the conference bridge. This could be a link to a web conference or Slack channel."
                  }
                }
              },
              "service": {
                "type": "object",
                "description": "Assign the incident to this service.",
                "properties": {
                  "id": {
                    "type": "string",
                    "description": "The ID of the service."
                  },
                  "type": {
                    "type": "string",
                    "description": "The type of the reference.",
                    "enum": [
                      "service_reference"
                    ]
                  }
                },
                "required": [
                  "id",
                  "type"
                ]
              }
            },
            "required": [
              "type"
            ]
          }
        },
        "required": [
          "incident"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "incident": {
              "type": "incident_reference",
              "status": "acknowledged"
            }
          }
        }
      }
    }
  }
}
