http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "alerts": {
            "type": "array",
            "description": "An array of alerts, including the parameters to update for each alert.",
            "items": {
              "type": "object",
              "x-examples": {
                "Example 1": {
                  "status": "resolved",
                  "incident": {
                    "id": "string",
                    "type": "incident_reference"
                  }
                }
              },
              "properties": {
                "status": {
                  "type": "string",
                  "enum": [
                    "resolved",
                    "triggered"
                  ]
                },
                "incident": {
                  "type": "object",
                  "x-examples": {
                    "Example 1": {
                      "id": "string",
                      "type": "incident_reference"
                    }
                  },
                  "properties": {
                    "id": {
                      "type": "string"
                    },
                    "type": {
                      "type": "string",
                      "enum": [
                        "incident_reference"
                      ]
                    }
                  },
                  "required": [
                    "id"
                  ]
                }
              }
            }
          }
        },
        "required": [
          "alerts"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "alerts": [
              {
                "id": "PPVZH9X",
                "type": "alert",
                "status": "resolved"
              },
              {
                "id": "P8JOGX7",
                "type": "alert",
                "incident": {
                  "id": "PPVZH9X",
                  "type": "incident_reference"
                }
              }
            ]
          }
        }
      }
    }
  }
}
