http_endpoint.request_body_spec:
{
  "description": "The feature enablement setting to apply.",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "enablement": {
            "type": "object",
            "properties": {
              "feature": {
                "readOnly": true,
                "type": "string",
                "description": "The name of the product addon whose set of features will be enabled or disabled.",
                "example": "aiops"
              },
              "enabled": {
                "type": "boolean",
                "description": "A boolean value indicating whether the specified product addon is enabled or disabled."
              },
              "updated_at": {
                "readOnly": true,
                "type": "string",
                "format": "date-time",
                "description": "The time the feature enablement was last updated."
              },
              "warnings": {
                "readOnly": true,
                "type": "array",
                "description": "An array of warnings related to this feature enablement. Only present if warning conditions are met.",
                "items": {
                  "type": "object",
                  "properties": {
                    "message": {
                      "type": "string",
                      "description": "The warning message."
                    }
                  }
                }
              }
            },
            "required": [
              "enabled"
            ]
          }
        },
        "required": [
          "enablement"
        ]
      },
      "examples": {
        "enable_aiops": {
          "$ref": "#/components/examples/FeatureEnablementPutRequestEnable"
        },
        "disable_aiops": {
          "$ref": "#/components/examples/FeatureEnablementPutRequestDisable"
        }
      }
    }
  }
}
