http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "extension": {
            "allOf": [
              {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "readOnly": true
                  },
                  "summary": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                  },
                  "type": {
                    "type": "string",
                    "readOnly": true,
                    "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                  },
                  "self": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "the API show URL at which the object is accessible"
                  },
                  "html_url": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "a URL at which the entity is uniquely displayed in the Web app"
                  }
                }
              },
              {
                "type": "object",
                "properties": {
                  "name": {
                    "type": "string",
                    "description": "The name of the extension."
                  },
                  "type": {
                    "type": "string",
                    "description": "The type of object being created.",
                    "default": "extension",
                    "enum": [
                      "extension"
                    ]
                  },
                  "endpoint_url": {
                    "type": "string",
                    "format": "url",
                    "description": "The url of the extension."
                  },
                  "extension_objects": {
                    "type": "array",
                    "description": "The objects for which the extension applies",
                    "items": {
                      "allOf": [
                        {
                          "allOf": [
                            {
                              "type": "object",
                              "properties": {
                                "id": {
                                  "type": "string",
                                  "readOnly": true
                                },
                                "summary": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                },
                                "type": {
                                  "type": "string",
                                  "readOnly": true,
                                  "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                },
                                "self": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "format": "url",
                                  "description": "the API show URL at which the object is accessible"
                                },
                                "html_url": {
                                  "type": "string",
                                  "nullable": true,
                                  "readOnly": true,
                                  "format": "url",
                                  "description": "a URL at which the entity is uniquely displayed in the Web app"
                                }
                              }
                            },
                            {
                              "type": "object",
                              "required": [
                                "type",
                                "id"
                              ]
                            }
                          ]
                        },
                        {
                          "type": "object",
                          "properties": {
                            "type": {
                              "type": "string",
                              "enum": [
                                "service_reference"
                              ]
                            }
                          }
                        }
                      ]
                    }
                  },
                  "extension_schema": {
                    "allOf": [
                      {
                        "allOf": [
                          {
                            "type": "object",
                            "properties": {
                              "id": {
                                "type": "string",
                                "readOnly": true
                              },
                              "summary": {
                                "type": "string",
                                "nullable": true,
                                "readOnly": true,
                                "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                              },
                              "type": {
                                "type": "string",
                                "readOnly": true,
                                "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                              },
                              "self": {
                                "type": "string",
                                "nullable": true,
                                "readOnly": true,
                                "format": "url",
                                "description": "the API show URL at which the object is accessible"
                              },
                              "html_url": {
                                "type": "string",
                                "nullable": true,
                                "readOnly": true,
                                "format": "url",
                                "description": "a URL at which the entity is uniquely displayed in the Web app"
                              }
                            }
                          },
                          {
                            "type": "object",
                            "required": [
                              "type",
                              "id"
                            ]
                          }
                        ]
                      },
                      {
                        "type": "object",
                        "properties": {
                          "type": {
                            "type": "string",
                            "enum": [
                              "extension_schema_reference"
                            ]
                          }
                        }
                      }
                    ]
                  },
                  "temporarily_disabled": {
                    "type": "boolean",
                    "readOnly": true,
                    "description": "Whether or not this extension is temporarily disabled; for example, a webhook extension that is repeatedly rejected by the server.",
                    "default": false
                  },
                  "config": {
                    "type": "object",
                    "description": "The object that contains extension configuration values depending on the extension schema specification."
                  }
                },
                "required": [
                  "extension_objects",
                  "extension_schema",
                  "name"
                ],
                "example": {
                  "id": "PJU23I3",
                  "endpoint_url": "https://example.com/receive_a_pagerduty_webhook",
                  "name": "My Webhook",
                  "summary": "My Webhook",
                  "type": "extension",
                  "extension_schema": {
                    "id": "PJFWPEP",
                    "type": "extension_schema_reference"
                  },
                  "extension_objects": [
                    {
                      "id": "PIJ90N7",
                      "type": "service_reference"
                    }
                  ],
                  "config": {
                    "anykey": "anyvalue"
                  }
                }
              }
            ]
          }
        },
        "required": [
          "extension"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "extension": {
              "endpoint_url": "https://example.com/receive_a_pagerduty_webhook",
              "name": "My Webhook",
              "extension_schema": {
                "id": "PJFWPEP",
                "type": "extension_schema_reference"
              },
              "extension_objects": [
                {
                  "id": "PIJ90N7",
                  "type": "service_reference"
                }
              ]
            }
          }
        },
        "requestCustomHeaders": {
          "summary": "Request Example with Custom Headers",
          "value": {
            "extension": {
              "endpoint_url": "https://example.com/receive_a_pagerduty_webhook",
              "name": "My Webhook",
              "extension_schema": {
                "id": "PJFWPEP",
                "type": "extension_schema_reference"
              },
              "extension_objects": [
                {
                  "id": "PIJ90N7",
                  "type": "service_reference"
                }
              ],
              "config": {
                "headers": [
                  {
                    "name": "Authorization",
                    "value": "Token token=super_secret_token_value"
                  }
                ]
              }
            }
          }
        }
      }
    }
  },
  "description": "The extension to be updated."
}
