http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "field": {
            "type": "object",
            "description": "Details of the custom field to be updated.",
            "properties": {
              "display_name": {
                "type": "string",
                "description": "The display name of the Incident Type.",
                "maxLength": 50
              },
              "enabled": {
                "type": "boolean",
                "description": "Whether the Incident Type is enabled."
              },
              "default_value": {
                "type": "string",
                "description": "The default value of the custom field."
              },
              "description": {
                "type": "string",
                "description": "The description of the custom field.",
                "maxLength": 1000
              },
              "field_options": {
                "type": "array",
                "items": {
                  "allOf": [
                    {
                      "type": "object",
                      "properties": {
                        "data": {
                          "oneOf": [
                            {
                              "type": "object",
                              "properties": {
                                "data_type": {
                                  "type": "string",
                                  "description": "The kind of data represented by this option. Must match the Field's `data_type`.",
                                  "enum": [
                                    "string"
                                  ]
                                },
                                "value": {
                                  "type": "string",
                                  "maxLength": 200
                                }
                              },
                              "required": [
                                "data_type",
                                "value"
                              ]
                            }
                          ],
                          "discriminator": {
                            "propertyName": "data_type",
                            "mapping": {
                              "string": "#/paths/~1incidents~1custom_fields/get/responses/200/content/application~1json/schema/allOf/0/properties/fields/items/allOf/0/properties/field_options/items/allOf/0/properties/data/oneOf/0"
                            }
                          }
                        },
                        "id": {
                          "type": "string",
                          "readOnly": true,
                          "description": "The ID of the resource."
                        },
                        "type": {
                          "type": "string",
                          "enum": [
                            "field_option"
                          ],
                          "readOnly": true
                        },
                        "updated_at": {
                          "type": "string",
                          "format": "date-time",
                          "description": "The date/time the object was last updated.",
                          "readOnly": true
                        },
                        "created_at": {
                          "type": "string",
                          "format": "date-time",
                          "description": "The date/time the object was created at.",
                          "readOnly": true
                        }
                      },
                      "required": [
                        "id",
                        "type",
                        "created_at",
                        "updated_at"
                      ],
                      "description": ""
                    },
                    {
                      "type": "object",
                      "required": [
                        "id",
                        "data",
                        "created_at",
                        "updated_at"
                      ]
                    }
                  ]
                },
                "description": "List of options to upsert on the custom field. Can only be applied to fields with a `field_type` of `single_value_fixed` or `multi_value_fixed`. When an `id` property is included, this will update the value of the specified field option. Without an `id` property, that field option will be added to the field. Any existing field options not included in the upsert list will be deleted (unless the current default value refers to a field option to be deleted)."
              }
            }
          }
        },
        "required": [
          "field"
        ]
      },
      "examples": {
        "basic_example": {
          "summary": "Request Example",
          "value": {
            "field": {
              "display_name": "Custom Field 1"
            }
          }
        },
        "example_with_field_options_upsert": {
          "summary": "Example Updating a Field Option Together With Field Metadata",
          "value": {
            "field": {
              "display_name": "Single Select Field Updated",
              "field_options": [
                {
                  "data": {
                    "data_type": "string",
                    "value": "Updated value for an existing field option"
                  },
                  "id": "PQ9K7I8"
                },
                {
                  "data": {
                    "data_type": "string",
                    "value": "Upserting a brand new field option, by not providing an id property"
                  }
                }
              ]
            }
          }
        }
      }
    }
  }
}
