http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "custom_fields": {
            "type": "array",
            "title": "Array of Custom Field Values",
            "items": {
              "title": "Custom Field Value",
              "oneOf": [
                {
                  "type": "object",
                  "title": "Value by Field Name",
                  "properties": {
                    "name": {
                      "type": "string",
                      "title": "Field Name",
                      "description": "The name of the field. May include ASCII characters, specifically lowercase letters, digits, and underescores. The `name` for a Field must be unique and cannot be changed once created.",
                      "maxLength": 50
                    },
                    "value": {
                      "oneOf": [
                        {
                          "type": "object",
                          "title": "Boolean",
                          "properties": {
                            "value": {
                              "type": "boolean",
                              "nullable": true
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "Float",
                          "properties": {
                            "value": {
                              "type": "number",
                              "nullable": true
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "Integer",
                          "properties": {
                            "value": {
                              "type": "integer",
                              "nullable": true
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "String",
                          "properties": {
                            "value": {
                              "oneOf": [
                                {
                                  "type": "string",
                                  "maxLength": 200,
                                  "nullable": true
                                },
                                {
                                  "type": "array",
                                  "items": {
                                    "type": "string",
                                    "maxLength": 200
                                  },
                                  "maxItems": 10,
                                  "uniqueItems": true,
                                  "nullable": true
                                }
                              ]
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "Datetime",
                          "properties": {
                            "value": {
                              "type": "string",
                              "nullable": true,
                              "format": "date-time"
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "Url",
                          "properties": {
                            "value": {
                              "type": "string",
                              "format": "uri",
                              "maxLength": 200,
                              "nullable": true
                            }
                          }
                        }
                      ]
                    }
                  }
                },
                {
                  "type": "object",
                  "title": "Value by Field ID",
                  "properties": {
                    "id": {
                      "type": "string",
                      "description": "The ID of the Field."
                    },
                    "value": {
                      "oneOf": [
                        {
                          "type": "object",
                          "title": "Boolean",
                          "properties": {
                            "value": {
                              "type": "boolean",
                              "nullable": true
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "Float",
                          "properties": {
                            "value": {
                              "type": "number",
                              "nullable": true
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "Integer",
                          "properties": {
                            "value": {
                              "type": "integer",
                              "nullable": true
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "String",
                          "properties": {
                            "value": {
                              "oneOf": [
                                {
                                  "type": "string",
                                  "maxLength": 200,
                                  "nullable": true
                                },
                                {
                                  "type": "array",
                                  "items": {
                                    "type": "string",
                                    "maxLength": 200
                                  },
                                  "maxItems": 10,
                                  "uniqueItems": true,
                                  "nullable": true
                                }
                              ]
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "Datetime",
                          "properties": {
                            "value": {
                              "type": "string",
                              "nullable": true,
                              "format": "date-time"
                            }
                          }
                        },
                        {
                          "type": "object",
                          "title": "Url",
                          "properties": {
                            "value": {
                              "type": "string",
                              "format": "uri",
                              "maxLength": 200,
                              "nullable": true
                            }
                          }
                        }
                      ]
                    }
                  }
                }
              ]
            }
          }
        },
        "required": [
          "custom_fields"
        ]
      },
      "examples": {
        "example_with_custom_field_name": {
          "summary": "Request Example",
          "value": {
            "custom_fields": [
              {
                "name": "environment",
                "value": "production"
              }
            ]
          }
        },
        "example_with_custom_field_id": {
          "summary": "Request Example",
          "value": {
            "custom_fields": [
              {
                "id": "PT4KHEE",
                "value": "production"
              }
            ]
          }
        },
        "example_with_multiple_value_field": {
          "summary": "Request Example",
          "value": {
            "custom_fields": [
              {
                "id": "PT4KHEE",
                "value": [
                  "production",
                  "staging"
                ]
              }
            ]
          }
        }
      }
    }
  }
}
