http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "incident_type": {
            "type": "object",
            "description": "Details of the incident type to be created.",
            "properties": {
              "display_name": {
                "type": "string",
                "description": "The display name of the Incident Type.",
                "maxLength": 50
              },
              "enabled": {
                "type": "boolean",
                "description": "Whether the Incident Type is enabled. Defaults to true if not provided."
              },
              "description": {
                "type": "string",
                "description": "The description of the Incident Type.",
                "maxLength": 1000
              }
            }
          }
        },
        "required": [
          "incident_type"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "incident_type": {
              "display_name": "Major Incident"
            }
          }
        }
      }
    }
  }
}
