http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "trigger": {
            "allOf": [
              {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "readOnly": true
                  },
                  "summary": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                  },
                  "type": {
                    "type": "string",
                    "readOnly": true,
                    "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                  },
                  "self": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "the API show URL at which the object is accessible"
                  },
                  "html_url": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "a URL at which the entity is uniquely displayed in the Web app"
                  }
                }
              },
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "enum": [
                      "workflow_trigger"
                    ]
                  },
                  "trigger_type_name": {
                    "type": "string",
                    "readOnly": true,
                    "description": "Human readable name for the trigger type"
                  },
                  "condition": {
                    "type": "string",
                    "description": "A PCL condition string.\n\nIf specified, the trigger will execute when the condition is met on an incident.\n\nIf unspecified, the trigger will execute on incident creation.\n\nRequired if trigger_type is \u201cconditional\u201d, not allowed for other trigger types.\n"
                  },
                  "services": {
                    "type": "array",
                    "description": "An optional array of Services associated with this workflow. Incidents in any of the listed Services are eligible to fire this Trigger",
                    "items": {
                      "type": "object",
                      "properties": {
                        "id": {
                          "type": "string"
                        }
                      }
                    }
                  },
                  "is_subscribed_to_all_services": {
                    "type": "boolean",
                    "description": "Indicates that the Trigger should be associated with All Services"
                  },
                  "permissions": {
                    "description": "An object detailing who can start this Trigger. Applicable only to manual Triggers.",
                    "type": "object",
                    "properties": {
                      "restricted": {
                        "type": "boolean",
                        "description": "If true, indicates that the Trigger can only be started by authorized Users. If false, any user can start this Trigger. Applicable only to manual Triggers."
                      },
                      "team_id": {
                        "type": "string",
                        "description": "The ID of the team whose members can manually start this Trigger. Required and allowed if and only if permissions.restricted is true."
                      }
                    }
                  },
                  "is_disabled": {
                    "type": "boolean",
                    "description": "Indicates whether the Trigger is disabled or not. A previous API version allowed callers to update this\nproperty independently. This behavior is deprecated. To update this property set \"is_enabled\" on the workflow\nto which this trigger belongs.\n",
                    "deprecated": true
                  },
                  "incident_types": {
                    "type": "array",
                    "description": "An optional array of Incident Types associated with the trigger when it is of type `incident_type`.",
                    "items": {
                      "type": "string"
                    }
                  }
                }
              }
            ]
          }
        },
        "required": [
          "trigger"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "trigger": {
              "services": [
                {
                  "id": "PIJ90N7"
                }
              ],
              "is_subscribed_to_all_services": false,
              "condition": "incident.priority matches 'P1'"
            }
          }
        }
      }
    }
  }
}
